import React, {  useEffect } from 'react';
import { NavLink } from 'react-router-dom';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(true);
  const [notifications, setNotifications] = useState([]);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    const socket = new WebSocket('ws://localhost:8080');
  
    socket.onopen = () => {
      console.log('WebSocket connection established');
    };
  
    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'operation') {
        setNotifications((prevNotifications) => [
          ...prevNotifications,
          { message: data.message, timestamp: data.timestamp },
        ]);
      }
    };
  
    socket.onerror = (error) => {
      console.error('WebSocket error:', error);
    };
  
    socket.onclose = () => {
      console.log('WebSocket connection closed');
    };
  
    return () => {
      socket.close();
    };
  }, []);
  

  return (
    <aside
      className={`bg-gray-900 text-white p-6 shadow-md transform transition-transform duration-300 ease-in-out ${
        isOpen ? 'w-64' : 'w-20'
      }`}
    >
      <button onClick={toggleSidebar} className="text-white mb-6">
        {isOpen ? 'Close' : 'Open'}
      </button>
      {isOpen && (
        <div>
          <h2 className="text-xl font-bold mb-6">Navigation</h2>
          <ul className="space-y-6">
          // ...existing code...
<li>
  <NavLink
    to="/database-monitoring"
    activeClassName="text-blue-400"
    className="hover:text-blue-400 transition-colors"
  >
    Database Monitoring
  </NavLink>
</li>
// ...existing code...
            <li>
              <NavLink
                to="/data-visualization"
                activeClassName="text-blue-400"
                className="hover:text-blue-400 transition-colors"
              >
                Data Visualization
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/manage-data"
                activeClassName="text-blue-400"
                className="hover:text-blue-400 transition-colors"
              >
                Manage Data
              </NavLink>
            </li>
          </ul>

          <h2 className="text-xl font-bold mt-6 mb-4">Notifications</h2>
<ul className="space-y-4 overflow-y-auto max-h-64">
  {notifications.map((notification, index) => (
    <li
      key={index}
      className="bg-gray-800 p-4 rounded-lg shadow-md transition-all duration-300 hover:bg-gray-700"
    >
      <div className="text-sm text-gray-400">
        {new Date(notification.timestamp).toLocaleString()}
      </div>
      <div className="text-white">{notification.message}</div>
    </li>
  ))}
</ul>
<button
  onClick={() => setNotifications([])}
  className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-500"
>
  Effacer les notifications
</button>

        </div>
      )}
    </aside>
  );
};

export default Sidebar;
