import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import DemoPage from './components/DemoPage';
import LoginPage from './components/LoginPage';
import MainPage from './components/MainPage';
import SgbdOverview from './components/SgbdOverview'; // Import SGBD Overview Page

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Login state

  // Function to handle successful login
  const handleLoginSuccess = () => {
    setIsLoggedIn(true); // Update login state
  };

  return (
    <Router>
      <div className="flex flex-col h-screen">
        {/* Navbar */}
        <Navbar />

        <div className="flex flex-1">
          <div className="flex-1 overflow-y-auto">
            <Routes>
              <Route path="/" element={<DemoPage />} />
              <Route
                path="/login"
                element={<LoginPage onLoginSuccess={handleLoginSuccess} />}
              />
              <Route
                path="/main"
                element={isLoggedIn ? <MainPage /> : <RedirectToLogin />}
              />
              {/* SGBD Overview Route */}
              <Route
                path="/sgbd-monitoring"
                element={isLoggedIn ? <SgbdOverview /> : <RedirectToLogin />}
              />
              {/* After login, show choice page */}
              <Route
                path="/choices"
                element={isLoggedIn ? <ChoicesPage /> : <RedirectToLogin />}
              />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
};

// Choice page after login, where the user selects between Database Monitoring or SGBD Monitoring
const ChoicesPage = () => {
  const navigate = useNavigate();

  const handleSelectMainPage = () => {
    navigate('/main'); // Navigate to the main dashboard (Database Monitoring)
  };

  const handleSelectSgbdOverview = () => {
    navigate('/sgbd-monitoring'); // Navigate to the SGBD overview page
  };

  return (
    <div className="flex justify-center items-center h-full space-x-10">
      <button
        onClick={handleSelectMainPage}
        className="bg-blue-500 text-white p-6 rounded-lg hover:bg-blue-600 transition"
      >
        Database Monitoring
      </button>
      <button
        onClick={handleSelectSgbdOverview}
        className="bg-green-500 text-white p-6 rounded-lg hover:bg-green-600 transition"
      >
        SGBD Monitoring
      </button>
    </div>
  );
};

// Redirect component for unauthenticated access
const RedirectToLogin = () => {
  const navigate = useNavigate();

  React.useEffect(() => {
    navigate('/login'); // Redirect to login page if not authenticated
  }, [navigate]);

  return null; // Render nothing while redirecting
};

export default App;
