import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';

const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({
    barChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    lineChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    pieChart: {
      series: [],
      options: {
        labels: [],
        theme: {
          palette: 'palette5',
        },
      },
    },
    areaChart: {
      series: [],
      options: {
        chart: {
          type: 'area',
          stacked: true,
          toolbar: { show: false },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
  });

 useEffect(() => {
  const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
  if (credentials) {
    const { host, username, password, database } = credentials;

    const fetchData = () => {
      fetch(`http://localhost:5000/api/database-metrics?host=${host}&username=${username}&password=${password}&database=${database}`)
        .then((response) => response.json())
        .then((data) => {
          if (data.success && Array.isArray(data.metrics) && data.metrics.length > 0) {
            const metrics = data.metrics;
            setData(metrics);

            // Update chartData
            setChartData({
              barChart: {
                series: [{ name: 'Row Count', data: metrics.map((item) => item.value || 0) }],
                options: {
                  ...chartData.barChart.options,
                  xaxis: { categories: metrics.map((item) => item.name) },
                },
              },
              lineChart: {
                series: [{ name: 'Row Count', data: metrics.map((item) => item.value || 0) }],
                options: {
                  ...chartData.lineChart.options,
                  xaxis: { categories: metrics.map((item) => item.name) },
                },
              },
              pieChart: {
                series: metrics.map((item) => item.value || 0),
                options: {
                  ...chartData.pieChart.options,
                  labels: metrics.map((item) => item.name),
                },
              },
              areaChart: {
                series: [{ name: 'Row Count', data: metrics.map((item) => item.value || 0) }],
                options: {
                  ...chartData.areaChart.options,
                  xaxis: { categories: metrics.map((item) => item.name) },
                },
              },
              radarChart: {
                series: [{ name: 'Metrics', data: metrics.map((item) => item.value || 0) }],
                options: {
                  ...chartData.radarChart.options,
                  xaxis: { categories: metrics.map((item) => item.name) },
                },
              },
              heatmapChart: {
                series: metrics.map((item) => ({
                  name: item.name,
                  data: [item.value || 0, (item.value || 0) * 0.9, (item.value || 0) * 1.1],
                })),
                options: {
                  ...chartData.heatmapChart.options,
                  xaxis: { categories: ['Metric 1', 'Metric 2', 'Metric 3'] },
                },
              },
              scatterChart: {
                series: metrics.map((item, index) => ({
                  name: item.name,
                  data: [[index, item.value || 0]],
                })),
                options: {
                  ...chartData.scatterChart.options,
                  xaxis: { categories: metrics.map((item) => item.name) },
                },
              },
            });
          } else {
            console.error('Invalid or empty metrics data');
          }
        })
        .catch((err) => console.error('Error fetching data:', err));
    };

    fetchData(); // Initial fetch
    const intervalId = setInterval(fetchData, 5000); // Poll every 5 seconds

    return () => clearInterval(intervalId); // Cleanup on component unmount
  }
}, [chartData]);


  return (
    <div className="main-page bg-gray-800 min-h-screen p-6">
      <button
        onClick={toggleSidebar}
        className="bg-purple-600 text-white p-3 rounded-lg hover:bg-purple-700 transition duration-300 transform hover:scale-105 mb-8"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-6">
        <h2 className="text-4xl font-bold text-white mb-8">Database Monitoring Dashboard</h2>

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {data.map((item, index) => (
            <div
              key={index}
              className="bg-gray-700 p-6 rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-300"
            >
              <h3 className="text-xl font-semibold text-purple-500">{item.name}</h3>
              <p className="text-gray-300">Query Execution Speed: {item.queryExecutionSpeed}</p>
              <p className="text-gray-300">CPU Usage: {item.cpuUsage}%</p>
              <p className="text-gray-300">Memory Usage: {item.memoryUsage} MB</p>
              <p className="text-gray-300">Response Time: {item.responseTime} ms</p>
              <p className="text-gray-300">Active Connections: {item.activeConnections}</p>
            </div>
          ))}
        </motion.div>

        <div className="mt-12 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 xl:grid-cols-3 gap-8">
          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Query Execution Speed</h3>
            <Chart
              options={chartData.barChart.options}
              series={chartData.barChart.series}
              type="bar"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Response Time Over Time</h3>
            <Chart
              options={chartData.lineChart.options}
              series={chartData.lineChart.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Active Connections</h3>
            <Chart
              options={chartData.pieChart.options}
              series={chartData.pieChart.series}
              type="pie"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">CPU and Memory Usage</h3>
            <Chart
              options={chartData.areaChart.options}
              series={chartData.areaChart.series}
              type="area"
              height={350}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
