import React, { useEffect, useState } from 'react';
import SgbdMetricsChart from './SgbdMetricsChart'; // Import the chart component

const SgbdOverview = () => {
  const [performanceMetrics, setPerformanceMetrics] = useState(null);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:8080');

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setPerformanceMetrics(data);
    };

    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    return () => {
      ws.close();
    };
  }, []);

  if (!performanceMetrics) {
    return <div className="text-center text-white">Loading...</div>;
  }

  return (
    <div className="bg-gray-900 min-h-screen p-8 font-sans">
      <h1 className="text-4xl font-extrabold text-white mb-8 tracking-tight text-center">SGBD Overview</h1>
      <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl transform hover:scale-105 transition-all duration-300">
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          <div className="bg-gray-800 p-4 rounded-lg shadow-md">
            <p className="text-lg text-gray-400"><strong>Threads Connected:</strong> {performanceMetrics.database.threadsConnected}</p>
          </div>
          <div className="bg-gray-800 p-4 rounded-lg shadow-md">
            <p className="text-lg text-gray-400"><strong>Total Connections:</strong> {performanceMetrics.database.totalConnections}</p>
          </div>
          <div className="bg-gray-800 p-4 rounded-lg shadow-md">
            <p className="text-lg text-gray-400"><strong>Uptime:</strong> {performanceMetrics.database.uptimeSeconds} seconds</p>
          </div>
          <div className="bg-gray-800 p-4 rounded-lg shadow-md">
            <p className="text-lg text-gray-400"><strong>Memory Usage:</strong> {((performanceMetrics.system.memoryUsage.used / performanceMetrics.system.memoryUsage.total) * 100).toFixed(2)}%</p>
          </div>
          <div className="bg-gray-800 p-4 rounded-lg shadow-md">
            <p className="text-lg text-gray-400"><strong>CPU Load:</strong> {performanceMetrics.system.cpuLoad}%</p>
          </div>
        </div>
        
        {/* Add the chart below the SGBD data */}
        {performanceMetrics && <SgbdMetricsChart data={performanceMetrics} />}
      </div>
    </div>
  );
};

export default SgbdOverview;
