import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';
import axios from 'axios';
const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [tableName, setTableName] = useState('');

  const createTable = async () => {
    try {
      await axios.post('/api/create-table', { tableName });
      alert(`Table "${tableName}" created successfully.`);
    } catch (error) {
      console.error('Error creating table:', error);
      alert('Unable to create table.');
    }
  };

  const deleteTable = async () => {
    try {
      await axios.delete('/api/delete-table', { data: { tableName } });
      alert(`Table "${tableName}" deleted successfully.`);
    } catch (error) {
      console.error('Error deleting table:', error);
      alert('Unable to delete table.');
    }
  };
  const [chartData, setChartData] = useState({
    barChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    lineChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    pieChart: {
      series: [],
      options: {
        labels: [],
        theme: {
          palette: 'palette5',
        },
      },
    },
    areaChart: {
      series: [],
      options: {
        chart: {
          type: 'area',
          stacked: true,
          toolbar: { show: false },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    radarChart: {
      series: [],
      options: {
        chart: {
          type: 'radar',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    heatmapChart: {
      series: [],
      options: {
        chart: {
          type: 'heatmap',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    scatterChart: {
      series: [],
      options: {
        chart: {
          type: 'scatter',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
  });

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
    if (credentials) {
      const { host, username, password, database } = credentials;

      const fetchData = () => {
        fetch(
          `http://localhost:5000/api/database-metrics?host=${host}&username=${username}&password=${password}&database=${database}`
        )
          .then((response) => response.json())
          .then((data) => {
            if (data.success && data.metrics.length > 0) {
              const metrics = data.metrics;
              setData(metrics);
              setChartData({
                barChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                lineChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                pieChart: {
                  series: metrics.map((item) => item.value),
                  options: {
                    labels: metrics.map((item) => item.name),
                  },
                },
                areaChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                radarChart: {
                  series: [{ name: 'Metrics', data: metrics.map((item) => item.value) }],
                  options: {
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                heatmapChart: {
                  series: metrics.map((item) => ({ name: item.name, data: [item.value] })),
                  options: {
                    xaxis: { categories: ['Metric'] },
                  },
                },
                scatterChart: {
                  series: metrics.map((item, index) => ({ name: item.name, data: [[index, item.value]] })),
                  options: {
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
              });
            }
          })
          .catch((err) => console.error('Error fetching data:', err));
      };

      fetchData();
      const intervalId = setInterval(fetchData, 5000);

      return () => clearInterval(intervalId);
    }
  }, []);

  return (
    <div className="flex bg-gray-900 min-h-screen">
      <button
        onClick={toggleSidebar}
        className="bg-gray-800 text-white p-4 rounded-lg hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-purple-600 transition-all duration-300 mb-6 transform hover:scale-105"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
        </svg>
      </button>

      <div className="flex-1 p-8">
        <h2 className="text-4xl font-extrabold text-white mb-8 tracking-tight text-center">
          Real-Time Database Monitoring Dashboard
        </h2>
        <p className="text-lg text-gray-400 text-center mb-12">
          Monitor your database performance, track key metrics, and ensure smooth operation with real-time insights.
        </p>

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.7 }}
        >
          {data.map((item, index) => (
            <div
              key={index}
              className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-2xl transform hover:scale-105 transition-all duration-300"
            >
              <div className="flex justify-between mb-2">
                <h3 className="text-2xl font-semibold text-purple-500">{item.name}</h3>
                <p className="text-gray-300 text-lg">{item.value}</p>
              </div>
            </div>
          ))}
        </motion.div>

        <div className="mt-12 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 xl:grid-cols-3 gap-8">
          <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl">
            <Chart options={chartData.barChart.options} series={chartData.barChart.series} type="bar" height={250} />
          </div>
          <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl">
            <Chart options={chartData.lineChart.options} series={chartData.lineChart.series} type="line" height={250} />
          </div>
          <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl">
            <Chart options={chartData.pieChart.options} series={chartData.pieChart.series} type="pie" height={250} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
