import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';

const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({
    barChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    lineChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    pieChart: {
      series: [],
      options: {
        labels: [],
        theme: {
          palette: 'palette5',
        },
      },
    },
    areaChart: {
      series: [],
      options: {
        chart: {
          type: 'area',
          stacked: true,
          toolbar: { show: false },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    radarChart: {
      series: [],
      options: {
        chart: {
          type: 'radar',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    heatmapChart: {
      series: [],
      options: {
        chart: {
          type: 'heatmap',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    scatterChart: {
      series: [],
      options: {
        chart: {
          type: 'scatter',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
  });

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
    if (credentials) {
      const { host, username, password, database } = credentials;

      const fetchData = () => {
        fetch(`http://localhost:5000/api/database-metrics?host=${host}&username=${username}&password=${password}&database=${database}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success && data.metrics.length > 0) {
              const metrics = data.metrics;
              setData(metrics);
              setChartData({
                barChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.barChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                lineChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.lineChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                pieChart: {
                  series: metrics.map((item) => item.value),
                  options: {
                    ...chartData.pieChart.options,
                    labels: metrics.map((item) => item.name),
                  },
                },
                areaChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.areaChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                radarChart: {
                  series: [{ name: 'Metrics', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.radarChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                heatmapChart: {
                  series: metrics.map((item, index) => ({ name: item.name, data: [item.value, item.value * 0.9, item.value * 1.1] })),
                  options: {
                    ...chartData.heatmapChart.options,
                    xaxis: { categories: ['Metric 1', 'Metric 2', 'Metric 3'] },
                  },
                },
                scatterChart: {
                  series: metrics.map((item, index) => ({ name: item.name, data: [[index, item.value]] })),
                  options: {
                    ...chartData.scatterChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
              });
            }
          })
          .catch((err) => console.error('Error fetching data:', err));
      };

      fetchData(); // Initial fetch
      const intervalId = setInterval(fetchData, 5000); // Poll every 5 seconds

      return () => clearInterval(intervalId); // Cleanup on component unmount
    }
  }, [chartData]);

  return (
    <div className="main-page bg-gray-900 min-h-screen p-8 font-sans">
      <button
        onClick={toggleSidebar}
        className="bg-gray-800 text-white p-4 rounded-lg hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-purple-600 transition-all duration-300 mb-6 transform hover:scale-105"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            d="M4 6h16M4 12h16M4 18h16"
          />
        </svg>
      </button>
  
      <div className="py-12 px-8">
        <h2 className="text-4xl font-semibold text-white mb-6 text-center">
          Dashboard de Suivi en Temps Réel de la Base de Données
        </h2>
        <p className="text-lg text-gray-300 text-center mb-12 max-w-3xl mx-auto">
          Surveillez la performance de votre base de données, suivez les métriques clés et assurez un fonctionnement optimal en temps réel.
        </p>
  
        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          {data.map((item, index) => (
            <div
              key={index}
              className="bg-gray-800 p-6 rounded-lg shadow-md hover:shadow-lg transition-all duration-300"
            >
              <div className="flex justify-between mb-2">
                <h3 className="text-xl font-medium text-purple-500">{item.name}</h3>
                <p className="text-gray-300 text-lg">{item.value}</p>
              </div>
            </div>
          ))}
        </motion.div>
  
        <div className="mt-12 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 xl:grid-cols-3 gap-8">
          <div className="bg-gray-800 p-6 rounded-lg shadow-md hover:shadow-lg transition-all duration-300">
            <h3 className="text-2xl font-semibold text-white mb-4">Métriques de Performance de la Base de Données</h3>
            <p className="text-sm text-gray-400 mb-6">
              Suivez la performance de votre base de données, y compris la vitesse des requêtes et la charge du serveur.
            </p>
            <Chart
              options={chartData.barChart.options}
              series={chartData.barChart.series}
              type="bar"
              height={250}
            />
          </div>
  
          <div className="bg-gray-800 p-6 rounded-lg shadow-md hover:shadow-lg transition-all duration-300">
            <h3 className="text-2xl font-semibold text-white mb-4">Performances de la Base de Données au Fil du Temps</h3>
            <p className="text-sm text-gray-400 mb-6">
              Visualisez les tendances de performance sur une période pour comprendre la santé à long terme de votre base de données.
            </p>
            <Chart
              options={chartData.lineChart.options}
              series={chartData.lineChart.series}
              type="line"
              height={250}
            />
          </div>
  
          <div className="bg-gray-800 p-6 rounded-lg shadow-md hover:shadow-lg transition-all duration-300">
            <h3 className="text-2xl font-semibold text-white mb-4">Répartition des Métriques de la Base de Données</h3>
            <p className="text-sm text-gray-400 mb-6">
              Affichez la répartition des métriques telles que les types de requêtes, les temps de réponse et l’utilisation du serveur.
            </p>
            <Chart
              options={chartData.pieChart.options}
              series={chartData.pieChart.series}
              type="pie"
              height={250}
            />
          </div>
  
          <div className="bg-gray-800 p-6 rounded-lg shadow-md hover:shadow-lg transition-all duration-300">
            <h3 className="text-2xl font-semibold text-white mb-4">Tendances d'Activité de la Base de Données</h3>
            <p className="text-sm text-gray-400 mb-6">
              Obtenez des informations sur les tendances d'activité de votre base de données, y compris les requêtes et la charge serveur.
            </p>
            <Chart
              options={chartData.areaChart.options}
              series={chartData.areaChart.series}
              type="area"
              height={250}
            />
          </div>
  
          <div className="bg-gray-800 p-6 rounded-lg shadow-md hover:shadow-lg transition-all duration-300">
            <h3 className="text-2xl font-semibold text-white mb-4">Comparaison des Métriques</h3>
            <p className="text-sm text-gray-400 mb-6">
              Comparez les métriques clés pour évaluer les différences de performance et prendre des décisions basées sur les données.
            </p>
            <Chart
              options={chartData.radarChart.options}
              series={chartData.radarChart.series}
              type="radar"
              height={250}
            />
          </div>
  
          <div className="bg-gray-800 p-6 rounded-lg shadow-md hover:shadow-lg transition-all duration-300">
            <h3 className="text-2xl font-semibold text-white mb-4">Analyse des Heatmaps</h3>
            <p className="text-sm text-gray-400 mb-6">
              Analysez les points chauds d'activité à l’aide de heatmaps pour identifier les zones à forte charge serveur ou requêtes.
            </p>
            <Chart
              options={chartData.heatmapChart.options}
              series={chartData.heatmapChart.series}
              type="heatmap"
              height={250}
            />
          </div>
  
          <div className="bg-gray-800 p-6 rounded-lg shadow-md hover:shadow-lg transition-all duration-300">
            <h3 className="text-2xl font-semibold text-white mb-4">Graphiques de Dispersion</h3>
            <p className="text-sm text-gray-400 mb-6">
              Explorez les corrélations entre diverses métriques et la performance de la base de données à travers des graphiques de dispersion.
            </p>
            <Chart
              options={chartData.scatterChart.options}
              series={chartData.scatterChart.series}
              type="scatter"
              height={250}
            />
          </div>
        </div>
      </div>
    </div>
  );
  
  
  
};

export default MainPage;
