import React, { useEffect } from 'react';
import debounce from 'lodash.debounce';

const DemoPage = () => {
  useEffect(() => {
    const handleScroll = debounce(() => {
      const sections = document.querySelectorAll('.fadeIn');
      const scrollPosition = window.scrollY + window.innerHeight;

      sections.forEach((section, index) => {
        const isVisible = section.offsetTop < scrollPosition - 100;

        if (isVisible) {
          section.classList.add('opacity-100');
          section.classList.add(index % 2 === 0 ? 'animate-bottom-to-top' : 'animate-top-to-bottom');
        } else {
          section.classList.remove('opacity-100');
          section.classList.remove('animate-bottom-to-top', 'animate-top-to-bottom');
        }
      });
    }, 100);

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="bg-gradient-to-br from-gray-900 to-gray-800 text-white min-h-screen w-full">
      {/* Header Section */}
      <header className="relative py-40 text-center bg-gradient-to-r from-yellow-600 to-orange-600">
        {/* Background Gradient */}
        <div className="absolute top-0 left-0 w-full h-full bg-opacity-70 bg-gradient-to-r from-yellow-700 to-orange-700"></div>

        {/* Main Content */}
        <div className="relative z-10 text-white">
          <h1 className="text-6xl font-extrabold leading-tight tracking-wider mb-4">
            Welcome to <span className="text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-orange-300">DBVision</span>
          </h1>
          <p className="text-lg text-gray-200 max-w-3xl mx-auto mt-6 leading-relaxed">
            Experience next-level database monitoring with real-time insights, dynamic visualizations, and intuitive controls to manage your data.
          </p>
        </div>
      </header>

      {/* Features Section */}
      <section className="py-20 px-6 sm:px-8 lg:px-12 space-y-12 bg-gray-800">
        <div className="fixed top-0 right-0 h-0 w-1 bg-yellow-500 z-50 scrollProgressLine transition-all duration-300"></div>

        {/* Feature Cards */}
        {[{
            title: "Interactive Dashboards",
            description: "Visualize your data with real-time charts that update dynamically as your database changes.",
            icon: "ðŸ“Š",
          },
          {
            title: "Manage Your Data",
            description: "Filter, query, and modify your database with intuitive and responsive controls.",
            icon: "âš™ï¸",
          },
          {
            title: "Real-Time Updates",
            description: "Stay updated on your databaseâ€™s performance with live insights, ensuring youâ€™re always in control.",
            icon: "â±ï¸",
          },
        ].map((feature, index) => (
          <div
            key={index}
            className="bg-gray-700 rounded-lg shadow-2xl p-8 opacity-0 fadeIn transform transition-opacity duration-1000 animate__animated animate__fadeIn animate__delay-2s"
          >
            <div className="flex items-center mb-4">
              <div className="text-5xl mr-4 text-yellow-400">{feature.icon}</div>
              <h3 className="text-3xl font-semibold text-yellow-400">{feature.title}</h3>
            </div>
            <p className="text-gray-300 text-lg">{feature.description}</p>
          </div>
        ))}
      </section>

      {/* Monitoring Section */}
      <section className="py-20 px-6 sm:px-8 lg:px-12 space-y-12 bg-gray-800 rounded-lg shadow-xl">
        <h2 className="text-3xl font-semibold text-yellow-400 text-center mb-6">
          Real-Time Database Monitoring
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {[{
              title: "Performance Analytics",
              icon: "ðŸ“ˆ",
              description: "Monitor the health of your database with detailed performance graphs in real-time.",
            },
            {
              title: "Data Usage",
              icon: "ðŸ’¾",
              description: "Track and visualize storage usage, keeping your database efficient and optimized.",
            },
            {
              title: "Query Insights",
              icon: "ðŸ”",
              description: "Gain deep insights into frequent queries, understanding their impact on performance.",
            },
          ].map((item, index) => (
            <div
              key={index}
              className="bg-gray-700 p-6 rounded-lg shadow-lg opacity-0 fadeIn transform transition-opacity duration-1000 animate__animated animate__fadeIn animate__delay-3s"
            >
              <div className="text-4xl mb-4 text-yellow-400">{item.icon}</div>
              <h3 className="text-2xl font-semibold text-yellow-400 mb-2">{item.title}</h3>
              <p className="text-gray-300">{item.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Call-to-Action Section */}
      <section className="flex justify-center py-12 bg-gradient-to-r from-gray-900 to-gray-800">
        <button
          className="bg-yellow-600 hover:bg-yellow-700 text-white text-xl font-semibold py-4 px-10 rounded-full shadow-lg transform hover:scale-105 transition-transform duration-300 animate__animated animate__zoomIn"
          onClick={() => window.location.href = '/login'}
        >
          Connect Your Database
        </button>
      </section>

      {/* Footer Section */}
      <footer className="py-6 text-center bg-gray-800">
        <p className="text-gray-400 text-sm">
          DBVision - All Rights Reserved | &copy; {new Date().getFullYear()}
        </p>
      </footer>
    </div>
  );
};

export default DemoPage;
