import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import DemoPage from './components/DemoPage';
import LoginPage from './components/LoginPage';
import MainPage from './components/MainPage';

const App = () => {
  const [isSidebarVisible, setIsSidebarVisible] = useState(false); // Manage sidebar visibility state

  const toggleSidebar = () => {
    setIsSidebarVisible((prev) => !prev); // Toggle sidebar visibility
  };

  return (
    <Router>
      <div className="flex flex-col h-screen">
        {/* Navbar */}
        <Navbar />

        <div className="flex flex-1">
          {/* Sidebar */}
          <Sidebar isVisible={isSidebarVisible} />

          <div className="flex-1 overflow-y-auto p-4">
            <Routes>
              <Route path="/" element={<DemoPage />} />
              <Route path="/login" element={<LoginPage />} />
              {/* Pass toggleSidebar to MainPage */}
              <Route path="/main" element={<MainPage toggleSidebar={toggleSidebar} />} />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
};

export default App;
