import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';
import { useSearchParams } from 'react-router-dom';

const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [selectedMetrics, setSelectedMetrics] = useState([]);
  const [chartData, setChartData] = useState({
    barChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: true },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
          zoom: { enabled: true },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
        dataLabels: {
          enabled: true,
        },
        tooltip: {
          enabled: true,
        },
      },
    },
    lineChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: true },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
          zoom: { enabled: true },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
        dataLabels: {
          enabled: true,
        },
        tooltip: {
          enabled: true,
        },
      },
    },
    pieChart: {
      series: [],
      options: {
        labels: [],
        theme: {
          palette: 'palette5',
        },
        dataLabels: {
          enabled: true,
        },
        tooltip: {
          enabled: true,
        },
      },
    },
    areaChart: {
      series: [],
      options: {
        chart: {
          type: 'area',
          stacked: true,
          toolbar: { show: true },
          zoom: { enabled: true },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
        dataLabels: {
          enabled: true,
        },
        tooltip: {
          enabled: true,
        },
      },
    },
    radarChart: {
      series: [],
      options: {
        chart: {
          type: 'radar',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
        dataLabels: {
          enabled: true,
        },
        tooltip: {
          enabled: true,
        },
      },
    },
    heatmapChart: {
      series: [],
      options: {
        chart: {
          type: 'heatmap',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
        dataLabels: {
          enabled: true,
        },
        tooltip: {
          enabled: true,
        },
      },
    },
    scatterChart: {
      series: [],
      options: {
        chart: {
          type: 'scatter',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
        dataLabels: {
          enabled: true,
        },
        tooltip: {
          enabled: true,
        },
      },
    },
  });
  const [searchParams] = useSearchParams();

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
    if (credentials) {
      const { host, username, password, database } = credentials;

      const fetchData = () => {
        fetch(`http://localhost:5000/api/database-metrics?host=<span class="math-inline">\{host\}&username\=</span>{username}&password=<span class="math-inline">\{password\}&database\=</span>{database}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success && data.metrics.length > 0) {
              setData(data.metrics);
            }
          })
          .catch((err) => console.error('Error fetching data:', err));
      };

      fetchData(); // Initial fetch
      const intervalId = setInterval(fetchData, 5000); // Poll every 5 seconds

      return () => clearInterval(intervalId); // Cleanup on component unmount
    }
  }, []);

  useEffect(() => {
    const selectedMetricsFromUrl = searchParams.getAll('metric');
    setSelectedMetrics(selectedMetricsFromUrl);
    const filteredData = filterData(selectedMetricsFromUrl);
    updateCharts(filteredData);
  }, [data, searchParams]);

  const filterData = (metrics) => {
    if (!metrics.length) return data;
    return data.filter((item) => metrics.includes(item.name));
  };

  const updateCharts = (filteredData) => {
    setChartData({
      barChart: {
        series: [{ name: 'Row Count', data: filteredData.map((item) => item.value) }],
        options: {
          ...chartData.barChart.options,
          xaxis: { categories: filteredData.map((item) => item.name) },
        },
      },
      lineChart: {
        series: [{ name: 'Row Count', data: filteredData.map((item) => item.value) }],
        options: {
          ...chartData.lineChart.options,
          xaxis: { categories: filteredData.map((item) => item.name) },
        },
      },
      pieChart: {
        series: filteredData.map((item) => item.value),
        options: {
          ...chartData.pieChart.options,
          labels: filteredData.map((item) => item.name),
        },
      },
      areaChart: {
        series: [{ name: 'Row Count', data: filteredData.map((item) => item.value) }],
        options: {
          ...chartData.areaChart.options,
          xaxis: { categories: filteredData.map((item) => item.name) },
        },
      },
      radarChart: {
        series: [{ name: 'Metrics', data: filteredData.map((item) => item.value) }],
        options: {
          ...chartData.radarChart.options,
          xaxis: { categories: filteredData.map((item) => item.name) },
        },
      },
      heatmapChart: {
        series: filteredData.map((item, index) => ({
          name: item.name,
          data: [item.value, item.value * 0.9, item.value * 1.1],
        })),
        options: {
          ...chartData.heatmapChart.options,
          xaxis: { categories: ['Metric 1', 'Metric 2', 'Metric 3'] },
        },
      },
      scatterChart: {
        series: filteredData.map((item, index) => ({
          name: item.name,
          data: [[index, item.value]],
        })),
        options: {
          ...chartData.scatterChart.options,
          xaxis: { categories: filteredData.map((item) => item.name) },
        },
      },
    });
  };

  const handleMetricChange = (event) => {
    const newSelectedMetrics = [...selectedMetrics];
    if (event.target.checked) {
      newSelectedMetrics.push(event.target.value);
    } else {
      const index = newSelectedMetrics.indexOf(event.target.value);
      newSelectedMetrics.splice(index, 1);
    }
    setSelectedMetrics(newSelectedMetrics);
  };

  return (
    <div className="main-page bg-gray-900 min-h-screen p-4">
      <button
        onClick={toggleSidebar}
        className="bg-purple-600 text-white p-2 rounded-md hover:bg-purple-700 transition duration-300 transform hover:scale-105 mb-6"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-4">
        <h2 className="text-3xl font-bold text-white mb-6">Database Monitoring Dashboard</h2>

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {data.map((item, index) => (
            <div
              key={index}
              className="bg-gray-800 p-6 rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-300"
            >
              <h3 className="text-xl font-semibold text-purple-500">{item.name}</h3>
              <p className="text-gray-300">{item.value}</p>
            </div>
          ))}
        </motion.div>

        <div className="mt-12 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 xl:grid-cols-3 gap-8">
          {/* First half of the charts */}
          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Database Performance Metrics</h3>
            <Chart
              options={chartData.barChart.options}
              series={chartData.barChart.series}
              type="bar"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Database Performance Over Time</h3>
            <Chart
              options={chartData.lineChart.options}
              series={chartData.lineChart.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Database Metrics Distribution</h3>
            <Chart
              options={chartData.pieChart.options}
              series={chartData.pieChart.series}
              type="pie"
              height={350}
            />
          </div>

          {/* Second half of the charts */}
          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Database Activity Trends</h3>
            <Chart
              options={chartData.areaChart.options}
              series={chartData.areaChart.series}
              type="area"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Metric Comparisons</h3>
            <Chart
              options={chartData.radarChart.options}
              series={chartData.radarChart.series}
              type="radar"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Heatmap Analysis</h3>
            <Chart
              options={chartData.heatmapChart.options}
              series={chartData.heatmapChart.series}
              type="heatmap"
              height={350}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
