import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';

const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({
    querySpeedChart: {
      series: [],
      options: {
        chart: {
          type: 'line',
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: { categories: [] },
        theme: { palette: 'palette5' },
      },
    },
    cpuUsageChart: {
      series: [],
      options: {
        chart: {
          type: 'radialBar',
          toolbar: { show: false },
        },
        plotOptions: {
          radialBar: {
            dataLabels: { name: { show: true }, value: { show: true } },
          },
        },
        theme: { palette: 'palette5' },
      },
    },
    memoryUsageChart: {
      series: [],
      options: {
        chart: { type: 'radialBar', toolbar: { show: false } },
        plotOptions: {
          radialBar: { dataLabels: { name: { show: true }, value: { show: true } } },
        },
        theme: { palette: 'palette5' },
      },
    },
    activeConnectionsChart: {
      series: [],
      options: {
        chart: { type: 'bar', toolbar: { show: false } },
        xaxis: { categories: [] },
        theme: { palette: 'palette5' },
      },
    },
    responseTimeChart: {
      series: [],
      options: {
        chart: {
          type: 'line',
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: { categories: [] },
        theme: { palette: 'palette5' },
      },
    },
  });

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
    if (credentials) {
      const { host, username, password, database } = credentials;
  
      const fetchData = () => {
        fetch(`http://localhost:5000/api/database-performance?host=${host}&username=${username}&password=${password}&database=${database}`)
          .then((response) => response.json())
          .then((data) => {
            console.log('Fetched data:', data);  // Add this log to check the response
            if (data.success && data.metrics) {
              const metrics = data.metrics;
              setData(metrics);
              setChartData({
                querySpeedChart: {
                  series: [{ name: 'Query Speed', data: metrics.map((item) => item.querySpeed) }],
                  options: {
                    ...chartData.querySpeedChart.options,
                    xaxis: { categories: metrics.map((item) => item.timestamp) },
                  },
                },
                cpuUsageChart: {
                  series: [{ name: 'CPU Usage', data: metrics.map((item) => item.cpuUsage) }],
                  options: {
                    ...chartData.cpuUsageChart.options,
                  },
                },
                memoryUsageChart: {
                  series: [{ name: 'Memory Usage', data: metrics.map((item) => item.memoryUsage) }],
                  options: {
                    ...chartData.memoryUsageChart.options,
                  },
                },
                activeConnectionsChart: {
                  series: [{ name: 'Active Connections', data: metrics.map((item) => item.activeConnections) }],
                  options: {
                    ...chartData.activeConnectionsChart.options,
                    xaxis: { categories: metrics.map((item) => item.timestamp) },
                  },
                },
                responseTimeChart: {
                  series: [{ name: 'Response Time', data: metrics.map((item) => item.responseTime) }],
                  options: {
                    ...chartData.responseTimeChart.options,
                    xaxis: { categories: metrics.map((item) => item.timestamp) },
                  },
                },
              });
            }
          })
          .catch((err) => console.error('Error fetching data:', err));
      };
  
      fetchData();
      const intervalId = setInterval(fetchData, 5000);
  
      return () => clearInterval(intervalId);
    }
  }, [chartData]);
  

  return (
    <div className="main-page bg-gradient-to-r from-blue-800 via-purple-700 to-pink-600 min-h-screen p-8">
      <button
        onClick={toggleSidebar}
        className="bg-yellow-500 text-white p-3 rounded-md hover:bg-yellow-600 transition duration-300 transform hover:scale-105 mb-6"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-4">
        <h2 className="text-4xl font-extrabold text-white mb-6">Database Performance Monitoring Dashboard</h2>

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {data.map((item, index) => (
            <div
              key={index}
              className="bg-gray-800 p-6 rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-300"
            >
              <h3 className="text-xl font-semibold text-yellow-500">{item.name}</h3>
              <p className="text-gray-300">{item.value}</p>
            </div>
          ))}
        </motion.div>

        <div className="mt-12 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 xl:grid-cols-3 gap-8">
          <div className="chart-container">
            <h3 className="text-2xl font-semibold text-white mb-4">Query Execution Speed</h3>
            <Chart
              options={chartData.querySpeedChart.options}
              series={chartData.querySpeedChart.series}
              type="line"
              height={350}
            />
          </div>

          <div className="chart-container">
            <h3 className="text-2xl font-semibold text-white mb-4">CPU Usage</h3>
            <Chart
              options={chartData.cpuUsageChart.options}
              series={chartData.cpuUsageChart.series}
              type="radialBar"
              height={350}
            />
          </div>

          <div className="chart-container">
            <h3 className="text-2xl font-semibold text-white mb-4">Memory Usage</h3>
            <Chart
              options={chartData.memoryUsageChart.options}
              series={chartData.memoryUsageChart.series}
              type="radialBar"
              height={350}
            />
          </div>

          <div className="chart-container">
            <h3 className="text-2xl font-semibold text-white mb-4">Active Connections</h3>
            <Chart
              options={chartData.activeConnectionsChart.options}
              series={chartData.activeConnectionsChart.series}
              type="bar"
              height={350}
            />
          </div>

          <div className="chart-container">
            <h3 className="text-2xl font-semibold text-white mb-4">Response Time</h3>
            <Chart
              options={chartData.responseTimeChart.options}
              series={chartData.responseTimeChart.series}
              type="line"
              height={350}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
