import React, { useEffect } from 'react';
import debounce from 'lodash.debounce';

const DemoPage = () => {
  useEffect(() => {
    const handleScroll = debounce(() => {
      const sections = document.querySelectorAll('.fadeIn');
      const scrollPosition = window.scrollY + window.innerHeight;

      sections.forEach((section, index) => {
        const isVisible = section.offsetTop < scrollPosition - 100;

        if (isVisible) {
          section.classList.add('opacity-100');
          section.classList.add(index % 2 === 0 ? 'animate-bottom-to-top' : 'animate-top-to-bottom');
        } else {
          section.classList.remove('opacity-100');
          section.classList.remove('animate-bottom-to-top', 'animate-top-to-bottom');
        }
      });
    }, 100);

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="bg-gray-900 text-white min-h-screen w-full relative overflow-hidden">
      {/* Light Accents on the Sides */}
      <div className="absolute top-0 left-0 w-2 h-full bg-gradient-to-b from-indigo-500 to-transparent animate-lightGlow z-10"></div>
      <div className="absolute top-0 right-0 w-2 h-full bg-gradient-to-b from-teal-500 to-transparent animate-lightGlow z-10"></div>

      {/* Header Section */}
      <header className="relative py-40 text-center bg-gradient-to-r from-gray-900 to-gray-800 overflow-hidden">
      {/* Glowing grid effect for DBVision inspiration */}
      <div className="absolute inset-0 bg-gradient-to-r from-indigo-500 to-transparent opacity-50 animate-pulse"></div>

      {/* Header Title */}
      <div className="relative z-20 text-white">
        <h1 className="text-6xl font-extrabold tracking-wider leading-tight mb-6 text-shadow-lg animate__animated animate__fadeIn animate__delay-1s">
          Bienvenue sur <span className="text-transparent bg-clip-text bg-gradient-to-r from-pink-400 to-blue-600">DBVision</span>
        </h1>

        {/* Tagline */}
        <p className="text-xl text-gray-300 max-w-4xl mx-auto mt-6 leading-relaxed opacity-90 transform transition-opacity duration-700 hover:opacity-100 animate__animated animate__fadeIn animate__delay-1s">
          Optimisez vos bases de données avec des graphiques interactifs et des analyses en temps réel pour des décisions éclairées.
        </p>

        {/* Animated Data Grid */}
        <div className="absolute inset-0 w-full h-full z-10 grid grid-cols-12 grid-rows-12 gap-4 pointer-events-none animate__animated animate__fadeIn animate__delay-2s">
          <div className="col-span-2 row-span-2 bg-transparent border-2 border-indigo-400 opacity-70"></div>
          <div className="col-span-4 row-span-4 bg-transparent border-2 border-teal-400 opacity-70"></div>
          <div className="col-span-3 row-span-3 bg-transparent border-2 border-yellow-400 opacity-70"></div>
        </div>

        {/* Brief Description */}
        <p className="text-md text-gray-400 mt-4 opacity-80 max-w-3xl mx-auto">
          DBVision vous aide à surveiller vos bases de données en temps réel avec des outils d'analyse avancés. Découvrez les performances de vos données avec des visuels dynamiques et des insights en temps réel.
        </p>
      </div>
    </header>

      {/* Features Section */}
      <section className="py-32 px-6 sm:px-8 lg:px-12 space-y-12 bg-gray-800 z-20">
        <h2 className="text-4xl font-semibold text-indigo-500 text-center mb-6 transform transition-all duration-500 hover:text-gradient">
          Explorez Nos Fonctionnalités
        </h2>

        {/* Feature Cards */}
        {[{
            title: "Tableaux de Bord Dynamiques",
            description: "Visualisez en temps réel vos données avec des graphiques interactifs qui se mettent à jour à chaque modification.",
            icon: "📊",
          },
          {
            title: "Gérer et Analyser Vos Données",
            description: "Ajustez vos données et effectuez des analyses avancées grâce à des outils puissants et intuitifs.",
            icon: "⚙️",
          },
          {
            title: "Suivi en Temps Réel",
            description: "Restez informé sur les performances de votre base de données avec des mises à jour en continu.",
            icon: "⏱️",
          },
        ].map((feature, index) => (
          <div
            key={index}
            className="bg-gray-700 rounded-lg shadow-xl p-8 opacity-0 fadeIn transform transition-opacity duration-1000 animate__animated animate__fadeIn animate__delay-2s hover:scale-105 hover:shadow-2xl hover:bg-gradient-to-r from-indigo-700 to-blue-600"
          >
            <div className="flex items-center mb-4">
              <div className="text-5xl mr-4 text-indigo-500 transform transition-all duration-300 hover:scale-125">{feature.icon}</div>
              <h3 className="text-3xl font-semibold text-indigo-400">{feature.title}</h3>
            </div>
            <p className="text-gray-300 text-lg">{feature.description}</p>
          </div>
        ))}
      </section>

      {/* Monitoring Section */}
      <section className="py-32 px-6 sm:px-8 lg:px-12 space-y-12 bg-gray-900 rounded-lg shadow-xl z-20">
        <h2 className="text-4xl font-semibold text-indigo-500 text-center mb-6">
          Surveillance en Temps Réel
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {[{
              title: "Performance Analytics",
              icon: "📈",
              description: "Suivez la santé de votre base de données avec des graphiques en temps réel.",
            },
            {
              title: "Utilisation des Données",
              icon: "💾",
              description: "Suivez la consommation de stockage et optimisez les ressources.",
            },
            {
              title: "Vue d'Ensemble des Requêtes",
              icon: "🔍",
              description: "Identifiez les requêtes les plus fréquentes et leur impact sur les performances.",
            },
          ].map((item, index) => (
            <div
              key={index}
              className="bg-gray-700 p-6 rounded-lg shadow-xl opacity-0 fadeIn transform transition-opacity duration-1000 animate__animated animate__fadeIn animate__delay-3s hover:scale-105 hover:shadow-2xl hover:bg-gradient-to-l from-blue-600 to-indigo-600"
            >
              <div className="text-4xl mb-4 text-indigo-500 transform transition-all duration-300 hover:scale-125">{item.icon}</div>
              <h3 className="text-2xl font-semibold text-indigo-400 mb-2">{item.title}</h3>
              <p className="text-gray-300">{item.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Call-to-Action Section */}
      <section className="flex justify-center py-12 bg-gradient-to-r from-indigo-700 to-blue-900 z-20">
        <button
          className="bg-indigo-600 hover:bg-indigo-700 text-white text-xl font-semibold py-4 px-10 rounded-full shadow-lg transform hover:scale-110 transition-all duration-300 animate__animated animate__zoomIn"
          onClick={() => window.location.href = '/login'}
        >
          Connecter Votre Base de Données
        </button>
      </section>

      {/* Footer Section */}
      <footer className="py-6 text-center bg-gray-900 z-20">
        <p className="text-gray-400 text-sm">
          DBVision - Tous droits réservés | &copy; {new Date().getFullYear()}
        </p>
      </footer>
    </div>
  );
};

export default DemoPage;
