import React, { useState, useEffect } from 'react';
import Chart from 'react-apexcharts';
import axios from 'axios';
import { motion } from 'framer-motion';

const MainPage = () => {
  const [chartData, setChartData] = useState({
    queryExecutionSpeed: { options: {}, series: [] },
    cpuUsage: { options: {}, series: [] },
    memoryUsage: { options: {}, series: [] },
    responseTime: { options: {}, series: [] },
    activeConnections: { options: {}, series: [] },
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/api/database-metrics', {
          params: {
            host: 'your_host',
            username: 'your_username',
            password: 'your_password',
            database: 'your_database',
          },
        });

        const { additionalMetrics } = response.data;

        setChartData({
          queryExecutionSpeed: {
            options: {
              chart: { id: 'queryExecutionSpeed' },
              xaxis: { categories: additionalMetrics.map((metric) => metric.timestamp) },
            },
            series: [{ name: 'Query Execution Speed', data: additionalMetrics.map((metric) => metric.query_execution_speed) }],
          },
          cpuUsage: {
            options: {
              chart: { id: 'cpuUsage' },
              xaxis: { categories: additionalMetrics.map((metric) => metric.timestamp) },
            },
            series: [{ name: 'CPU Usage', data: additionalMetrics.map((metric) => metric.cpu_usage) }],
          },
          memoryUsage: {
            options: {
              chart: { id: 'memoryUsage' },
              xaxis: { categories: additionalMetrics.map((metric) => metric.timestamp) },
            },
            series: [{ name: 'Memory Usage', data: additionalMetrics.map((metric) => metric.memory_usage) }],
          },
          responseTime: {
            options: {
              chart: { id: 'responseTime' },
              xaxis: { categories: additionalMetrics.map((metric) => metric.timestamp) },
            },
            series: [{ name: 'Response Time', data: additionalMetrics.map((metric) => metric.response_time) }],
          },
          activeConnections: {
            options: {
              chart: { id: 'activeConnections' },
              xaxis: { categories: additionalMetrics.map((metric) => metric.timestamp) },
            },
            series: [{ name: 'Active Connections', data: additionalMetrics.map((metric) => metric.active_connections) }],
          },
        });
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    const intervalId = setInterval(fetchData, 5000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="main-page bg-gray-900 min-h-screen p-4">
      <button
        onClick={() => {}}
        className="bg-purple-600 text-white p-2 rounded-md hover:bg-purple-700 transition duration-300 transform hover:scale-105 mb-6"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-4">
        <h2 className="text-3xl font-bold text-white mb-6">Database Monitoring Dashboard</h2>

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Query Execution Speed</h3>
            <Chart
              options={chartData.queryExecutionSpeed.options}
              series={chartData.queryExecutionSpeed.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">CPU Usage</h3>
            <Chart
              options={chartData.cpuUsage.options}
              series={chartData.cpuUsage.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Memory Usage</h3>
            <Chart
              options={chartData.memoryUsage.options}
              series={chartData.memoryUsage.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Response Time</h3>
            <Chart
              options={chartData.responseTime.options}
              series={chartData.responseTime.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Active Connections</h3>
            <Chart
              options={chartData.activeConnections.options}
              series={chartData.activeConnections.series}
              type="line"
              height={350}
            />
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default MainPage;
