import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts'; // Import ApexCharts

const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const metrics = [
    { name: 'Active Connections', value: rows[0].active_connections },
    { name: 'Database Size (GB)', value: rows[0].database_size_gb },
  ];
  res.json({ success: true, metrics });
  
  const [chartData, setChartData] = useState({
    barChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
        },
        xaxis: {
          categories: ['Active Connections', 'Queries Per Minute', 'Database Size (GB)'],
        },
        theme: {
          mode: 'dark',
        },
      },
    },
    lineChart: {
      series: [],
      options: {
        chart: {
          type: 'line',
          height: 350,
          toolbar: { show: false },
        },
        xaxis: {
          categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
        },
        theme: {
          mode: 'dark',
        },
      },
    },
    pieChart: {
      series: [],
      options: {
        chart: {
          type: 'pie',
          height: 350,
        },
        labels: ['Table A', 'Table B', 'Table C'],
        theme: {
          mode: 'dark',
        },
      },
    },
    radarChart: {
      series: [],
      options: {
        chart: {
          type: 'radar',
          height: 350,
        },
        xaxis: {
          categories: ['CPU Usage', 'Memory Usage', 'Disk I/O', 'Network Traffic'],
        },
        theme: {
          mode: 'dark',
        },
      },
    },
  });

  useEffect(() => {
    fetch('http://localhost:5000/api/database-metrics')
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          const metrics = data.metrics;
  
          // Update charts with metrics data
          setChartData({
            barChart: {
              series: [{ name: 'Metric Value', data: metrics.map((item) => item.value) }],
              options: {
                ...chartData.barChart.options,
                xaxis: { categories: metrics.map((item) => item.name) },
              },
            },
            // Update other charts as needed...
          });
        }
      })
      .catch((err) => console.error('Error fetching data:', err));
  }, []);
  
  
  
  

  return (
    <div className="main-page bg-gray-900 min-h-screen p-4">
      <button
        onClick={toggleSidebar}
        className="bg-purple-600 text-white p-2 rounded-md hover:bg-purple-700 transition duration-300 transform hover:scale-105 mb-6"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-4">
        <h2 className="text-3xl font-bold text-white mb-6">Database Monitoring Dashboard</h2>

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {data.map((item) => (
            <div
              key={item.name}
              className="bg-gray-800 p-6 rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-300"
            >
              <h3 className="text-xl font-semibold text-purple-500">{item.name}</h3>
              <p className="text-gray-300">{item.value}</p>
            </div>
          ))}
        </motion.div>

        {/* ApexCharts Bar Chart for Database Metrics */}
        <div className="mt-12">
          <h3 className="text-2xl font-semibold text-white mb-4">Database Performance Metrics</h3>
          <Chart
            options={chartData.barChart.options}
            series={chartData.barChart.series}
            type="bar"
            height={350}
          />
        </div>

        {/* Line Chart for Query Response Times */}
        <div className="mt-12">
          <h3 className="text-2xl font-semibold text-white mb-4">Query Response Times Trend</h3>
          <Chart
            options={chartData.lineChart.options}
            series={chartData.lineChart.series}
            type="line"
            height={350}
          />
        </div>

        {/* Pie Chart for Database Table Size Distribution */}
        <div className="mt-12">
          <h3 className="text-2xl font-semibold text-white mb-4">Database Table Size Distribution</h3>
          <Chart
            options={chartData.pieChart.options}
            series={chartData.pieChart.series}
            type="pie"
            height={350}
          />
        </div>

        {/* Radar Chart for Database Performance Metrics */}
        <div className="mt-12">
          <h3 className="text-2xl font-semibold text-white mb-4">Database Performance Metrics</h3>
          <Chart
            options={chartData.radarChart.options}
            series={chartData.radarChart.series}
            type="radar"
            height={350}
          />
        </div>
      </div>
    </div>
  );
};

export default MainPage;
