import React, { useEffect } from 'react';
import debounce from 'lodash.debounce';

const DemoPage = () => {
  useEffect(() => {
    const handleScroll = debounce(() => {
      const sections = document.querySelectorAll('.fadeIn');
      const scrollPosition = window.scrollY + window.innerHeight;

      sections.forEach((section, index) => {
        const isVisible = section.offsetTop < scrollPosition - 100;

        if (isVisible) {
          section.classList.add('opacity-100');
          section.classList.add(index % 2 === 0 ? 'animate-bottom-to-top' : 'animate-top-to-bottom');
        } else {
          section.classList.remove('opacity-100');
          section.classList.remove('animate-bottom-to-top', 'animate-top-to-bottom');
        }
      });
    }, 100);

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="bg-gray-900 text-white min-h-screen w-full">
      {/* Header Section */}
      <header className="relative py-36 text-center bg-gradient-to-r from-purple-900 to-indigo-700 shadow-lg overflow-hidden">
  {/* Background Effects */}
  <div className="absolute top-0 left-0 w-full h-full bg-opacity-60 bg-gradient-to-r from-blue-800 via-indigo-600 to-purple-700"></div>
  
  {/* Glowing Abstract Circles */}
  <div className="absolute top-1/3 left-1/4 transform -translate-x-1/4 -translate-y-1/3 w-64 h-64 rounded-full bg-gradient-to-r from-blue-500 to-indigo-400 opacity-40 animate-pulse z-0"></div>
  <div className="absolute top-1/4 right-1/3 transform translate-x-1/4 translate-y-1/4 w-48 h-48 rounded-full bg-gradient-to-r from-pink-500 to-purple-500 opacity-30 animate-pulse z-0"></div>

  {/* Main Header Content */}
  <h1 className="text-6xl font-extrabold text-white leading-tight tracking-wide drop-shadow-lg animate__animated animate__fadeIn animate__delay-1s">
    Welcome to <span className="text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-pink-600">DBVision</span>
  </h1>

  {/* Subheading */}
  <p className="text-xl max-w-4xl mx-auto text-gray-200 mt-6 animate__animated animate__fadeIn animate__delay-2s">
    Dive into real-time data visualization and effortlessly manage your database with interactive charts, monitoring tools, and dynamic reporting.
  </p>

  {/* Animated Call-to-Action Button */}
  <div className="mt-8">
    <a href="/get-started" className="inline-block bg-gradient-to-r from-green-400 to-teal-500 text-white font-semibold text-lg py-4 px-10 rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-300">
      Get Started with DBVision
    </a>
  </div>

  {/* Decorative Bottom Gradient Overlay */}
  <div className="absolute bottom-0 left-0 w-full h-24 bg-gradient-to-t from-gray-900 to-transparent"></div>
</header>


      {/* Features Section */}
      <section className="py-16 px-6 sm:px-8 lg:px-12 space-y-12">
        <div className="fixed top-0 right-0 h-0 w-1 bg-purple-500 z-50 scrollProgressLine transition-all duration-300"></div>

        {/* Feature Cards */}
        {[
          {
            title: "Interactive Dashboards",
            description:
              "Visualize your database data with real-time interactive charts that update dynamically as your database changes.",
            icon: "📊",
          },
          {
            title: "Manage Your Data",
            description:
              "Easily manage and analyze your database with intuitive controls that allow you to filter, query, and modify your data.",
            icon: "⚙️",
          },
          {
            title: "Real-Time Updates",
            description:
              "Stay up to date with your database’s performance and changes as the dashboard updates live, giving you immediate insights.",
            icon: "⏱️",
          },
        ].map((feature, index) => (
          <div
            key={index}
            className="bg-gray-800 rounded-lg shadow-lg p-8 opacity-0 fadeIn transform transition-opacity duration-1000 animate__animated animate__fadeIn animate__delay-2s"
          >
            <div className="flex items-center mb-4">
              <div className="text-5xl mr-4 text-purple-400">{feature.icon}</div>
              <h3 className="text-3xl font-semibold text-purple-400">{feature.title}</h3>
            </div>
            <p className="text-gray-300 text-lg">{feature.description}</p>
          </div>
        ))}
      </section>

      {/* Monitoring Section */}
      <section className="py-16 px-6 sm:px-8 lg:px-12 space-y-12 bg-gray-800 rounded-lg shadow-lg">
        <h2 className="text-3xl font-semibold text-purple-400 text-center mb-6">
          Database Monitoring in Real-Time
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {[
            {
              title: "Performance Analytics",
              icon: "📈",
              description: "Monitor the health of your database in real-time with detailed performance graphs.",
            },
            {
              title: "Data Usage",
              icon: "💾",
              description: "Track and visualize the storage usage of your database with clear, easy-to-understand charts.",
            },
            {
              title: "Query Insights",
              icon: "🔍",
              description: "Get insights into the most frequent queries and their impact on your database performance.",
            },
          ].map((item, index) => (
            <div
              key={index}
              className="bg-gray-700 p-6 rounded-lg shadow-lg opacity-0 fadeIn transform transition-opacity duration-1000 animate__animated animate__fadeIn animate__delay-3s"
            >
              <div className="text-4xl mb-4 text-purple-400">{item.icon}</div>
              <h3 className="text-2xl font-semibold text-purple-400 mb-2">{item.title}</h3>
              <p className="text-gray-300">{item.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Call-to-Action Section */}
      <section className="flex justify-center py-12 bg-gray-900">
        <button
          className="bg-blue-700 hover:bg-blue-800 text-white text-xl font-semibold py-4 px-10 rounded-full shadow-lg transform hover:scale-105 transition-transform duration-300 animate__animated animate__zoomIn"
          onClick={() => window.location.href = '/login'}
        >
          Connect Your Database
        </button>
      </section>

      {/* Footer Section */}
      <footer className="py-6 text-center bg-gray-800">
        <p className="text-gray-400 text-sm">
          DBVision - All Rights Reserved | &copy; {new Date().getFullYear()}
        </p>
      </footer>
    </div>
  );
};

export default DemoPage;
