import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import DemoPage from './components/DemoPage';
import LoginPage from './components/LoginPage';
import MainPage from './components/MainPage';
import PerformanceMetricCard from './components/PerformanceMetricCard';


const App = () => {
  const [isSidebarVisible, setIsSidebarVisible] = useState(false); // Sidebar visibility state
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Login state

  const toggleSidebar = () => {
    setIsSidebarVisible((prev) => !prev); // Toggle the sidebar visibility
  };

  // Function to handle successful login
  const handleLoginSuccess = () => {
    setIsLoggedIn(true); // Update login state
    setIsSidebarVisible(true); // Show sidebar after login
  };

  return (
    <Router>
      <div className="flex flex-col h-screen">
        {/* Navbar */}
        <Navbar />

        <div className="flex flex-1">
          {/* Conditionally render Sidebar */}
          {isSidebarVisible && <Sidebar />}

          <div className="flex-1 overflow-y-auto">
            <Routes>
              <Route path="/" element={<DemoPage />} />
              {/* Pass handleLoginSuccess to LoginPage */}
              <Route
                path="/login"
                element={<LoginPage onLoginSuccess={handleLoginSuccess} />}
              />
              {/* Restrict MainPage access if not logged in */}
              <Route
                path="/main"
                element={
                  isLoggedIn ? (
                    <MainPage toggleSidebar={toggleSidebar}    />

                  ) : (
                    <RedirectToLogin />
                  )
                }
              />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
};

// Redirect component for unauthenticated access
const RedirectToLogin = () => {
  const navigate = useNavigate();

  React.useEffect(() => {
    navigate('/login'); // Redirect to login page
  }, [navigate]);

  return null; // Render nothing while redirecting
};

export default App;
