import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';  // Import ApexCharts

const MainPage = ({ toggleSidebar }) => {  // Receive toggleSidebar as a prop
  const [data, setData] = useState([]);
  
  const chartOptions = {
    chart: {
      toolbar: {
        show: false, // Hide toolbar for a cleaner UI
      },
    },
    xaxis: {
      categories: ['January', 'February', 'March', 'April', 'May', 'June'],
    },
    theme: {
      mode: 'dark', // Set chart theme to dark
    },
  };

  const chartSeries = [
    {
      name: 'Users',
      data: [150, 200, 250, 300, 350, 400], // Sample data for Users
    },
    {
      name: 'Orders',
      data: [1200, 1100, 1300, 1400, 1600, 1700], // Sample data for Orders
    },
  ];

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));

    if (credentials) {
      // Simulate an API call or database fetch
      setData([
        { name: 'Users', value: 150 },
        { name: 'Orders', value: 1200 },
        { name: 'Products', value: 800 },
      ]);
    }
  }, []);

  return (
    <div className="main-page bg-gray-900 min-h-screen p-4">
      <button
        onClick={toggleSidebar}  // Toggle sidebar visibility
        className="bg-purple-600 text-white p-2 rounded-md hover:bg-purple-700 transition duration-300 transform hover:scale-105 mb-6"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-4">
        <h2 className="text-3xl font-bold text-white mb-6">Dashboard</h2>

        <motion.div 
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {data.map((item) => (
            <div
              key={item.name}
              className="bg-gray-800 p-6 rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-300"
            >
              <h3 className="text-xl font-semibold text-purple-500">{item.name}</h3>
              <p className="text-gray-300">{item.value}</p>
            </div>
          ))}
        </motion.div>

        {/* ApexCharts Bar Chart */}
        <div className="mt-12">
          <h3 className="text-2xl font-semibold text-white mb-4">User and Order Statistics</h3>
          <Chart
            options={chartOptions}
            series={chartSeries}
            type="bar"  // You can change the type to 'line' for a line chart, etc.
            height={350}
          />
        </div>
      </div>
    </div>
  );
};

export default MainPage;
