import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SgbdMetricsChart from './SgbdMetricsChart'; // Import the chart component

const SgbdOverview = () => {
  const [sgbdData, setSgbdData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [performanceMetrics, setPerformanceMetrics] = useState(null);
const [diskIO, setDiskIO] = useState([]);
const [networkTraffic, setNetworkTraffic] = useState([]);
const [innodbMetrics, setInnodbMetrics] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const dbCredentials = JSON.parse(localStorage.getItem('dbCredentials'));
        const response = await axios.get('http://localhost:5000/api/sgbd-overview', {
          params: { host: dbCredentials.host, username: dbCredentials.username, password: dbCredentials.password },
        });
        setSgbdData(response.data.sgbd);
      } catch (error) {
        console.error('Error fetching SGBD data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>SGBD Overview</h1>
      {sgbdData ? (
        <div>
          {/* Display SGBD Data */}
          <p><strong>Version:</strong> {sgbdData.version}</p>
          <p><strong>Uptime:</strong> {sgbdData.uptime} seconds</p>
          <p><strong>Total Queries:</strong> {sgbdData.totalQueries}</p>
          <p><strong>Open Tables:</strong> {sgbdData.openTables}</p>
          
          {/* Add the chart below the SGBD data */}
          <SgbdMetricsChart data={sgbdData} />
        </div>
      ) : (
        <p>Failed to load data</p>
      )}
    </div>
  );
};

export default SgbdOverview;
