import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';
import axios from 'axios';

const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({
    cpuUsageChart: { series: [{ data: [70] }], options: { chart: { type: 'radialBar' } } },
    memoryUsageChart: { series: [{ data: [50] }], options: { chart: { type: 'radialBar' } } },
    responseTimeChart: { series: [{ name: 'Response Time', data: [10, 20, 15, 8] }], options: { chart: { type: 'line' } } },
    activeConnectionsChart: { series: [{ data: [30] }], options: { chart: { type: 'bar' } } },
    diskUsageChart: { series: [{ data: [60] }], options: { chart: { type: 'donut' } } },
    errorRateChart: { series: [{ data: [5] }], options: { chart: { type: 'pie' } } },
    threadUsageChart: { series: [{ data: [80] }], options: { chart: { type: 'radialBar' } } },
    ioOperationsChart: { series: [{ data: [120] }], options: { chart: { type: 'line' } } },
    userSessionsChart: { series: [{ data: [150] }], options: { chart: { type: 'bar' } } },
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:5000/api/server-stats'); // Your backend API
        const data = response.data;

        setChartData({
          cpuUsageChart: { series: [{ data: [data.cpuUsage] }], options: { chart: { type: 'radialBar' } } },
          memoryUsageChart: { series: [{ data: [data.memoryUsage] }], options: { chart: { type: 'radialBar' } } },
          responseTimeChart: { series: [{ name: 'Response Time', data: data.responseTimes }], options: { chart: { type: 'line' } } },
          activeConnectionsChart: { series: [{ data: [data.activeConnections] }], options: { chart: { type: 'bar' } } },
          diskUsageChart: { series: [{ data: [data.diskUsage] }], options: { chart: { type: 'donut' } } },
          errorRateChart: { series: [{ data: [data.errorRate] }], options: { chart: { type: 'pie' } } },
          threadUsageChart: { series: [{ data: [data.threadUsage] }], options: { chart: { type: 'radialBar' } } },
          ioOperationsChart: { series: [{ data: [data.ioOperations] }], options: { chart: { type: 'line' } } },
          userSessionsChart: { series: [{ data: [data.userSessions] }], options: { chart: { type: 'bar' } } },
        });
      } catch (error) {
        console.error('Error fetching data:', error);
      }
      setLoading(false);
    };

    fetchData();
    const intervalId = setInterval(fetchData, 5000); // Fetch data every 5 seconds

    return () => clearInterval(intervalId); // Cleanup on component unmount
  }, []);

  return (
    <div className="main-page bg-gray-900 min-h-screen p-4">
      <button
        onClick={toggleSidebar}
        className="bg-purple-600 text-white p-2 rounded-md hover:bg-purple-700 transition duration-300 transform hover:scale-105 mb-6"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-4">
        <h2 className="text-3xl font-bold text-white mb-6">Database Monitoring Dashboard</h2>

        {loading && (
          <div className="text-center text-white text-xl mb-6">
            <div className="animate-pulse">Fetching New Data...</div>
          </div>
        )}

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {/* Render Updated Charts */}
          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">CPU Usage</h3>
            <Chart options={chartData.cpuUsageChart.options} series={chartData.cpuUsageChart.series} type="radialBar" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Memory Usage</h3>
            <Chart options={chartData.memoryUsageChart.options} series={chartData.memoryUsageChart.series} type="radialBar" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Response Times</h3>
            <Chart options={chartData.responseTimeChart.options} series={chartData.responseTimeChart.series} type="line" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Active Connections</h3>
            <Chart options={chartData.activeConnectionsChart.options} series={chartData.activeConnectionsChart.series} type="bar" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Disk Usage</h3>
            <Chart options={chartData.diskUsageChart.options} series={chartData.diskUsageChart.series} type="donut" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Error Rate</h3>
            <Chart options={chartData.errorRateChart.options} series={chartData.errorRateChart.series} type="pie" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Thread Usage</h3>
            <Chart options={chartData.threadUsageChart.options} series={chartData.threadUsageChart.series} type="radialBar" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">I/O Operations</h3>
            <Chart options={chartData.ioOperationsChart.options} series={chartData.ioOperationsChart.series} type="line" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">User Sessions</h3>
            <Chart options={chartData.userSessionsChart.options} series={chartData.userSessionsChart.series} type="bar" height={350} />
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default MainPage;
