import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LoginPage = ({ onLoginSuccess }) => {
  const [host, setHost] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [database, setDatabase] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault(); // Prevent form submission reload
    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password, database }),
      });
  
      const result = await response.json();
  
      if (result.success) {
        navigate('/dashboard'); // Redirect on success
      } else {
        setError(result.message); // Display backend error
      }
    } catch (error) {
      setError('An unexpected error occurred. Please try again.');
      console.error('Login error:', error);
    }
  };
  

  return (
    <div className="bg-gray-900 min-h-screen flex items-center justify-center px-4 py-8">
      <div className="bg-gray-800 rounded-lg shadow-lg p-8 sm:w-96 w-full">
        <h2 className="text-3xl font-extrabold text-center text-white mb-6">DBVision Login</h2>
        {error && <p className="text-red-500 text-center mb-4">{error}</p>}
        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label htmlFor="host" className="text-gray-300 font-semibold">
              Host:
            </label>
            <input
              type="text"
              id="host"
              value={host}
              onChange={(e) => setHost(e.target.value)}
              className="w-full mt-2 p-3 rounded-md bg-gray-700 text-white border border-gray-600 focus:outline-none focus:ring-2 focus:ring-purple-600"
              placeholder="Enter host"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="username" className="text-gray-300 font-semibold">
              Username:
            </label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full mt-2 p-3 rounded-md bg-gray-700 text-white border border-gray-600 focus:outline-none focus:ring-2 focus:ring-purple-600"
              placeholder="Enter username"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="password" className="text-gray-300 font-semibold">
              Password (optional):
            </label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full mt-2 p-3 rounded-md bg-gray-700 text-white border border-gray-600 focus:outline-none focus:ring-2 focus:ring-purple-600"
              placeholder="Enter password"
            />
          </div>
          <div className="mb-6">
            <label htmlFor="database" className="text-gray-300 font-semibold">
              Database:
            </label>
            <input
              type="text"
              id="database"
              value={database}
              onChange={(e) => setDatabase(e.target.value)}
              className="w-full mt-2 p-3 rounded-md bg-gray-700 text-white border border-gray-600 focus:outline-none focus:ring-2 focus:ring-purple-600"
              placeholder="Enter database"
              required
            />
          </div>
          <button
            type="submit"
            className="w-full py-3 px-6 bg-purple-600 text-white rounded-md shadow-md hover:bg-purple-700 transition duration-300 transform hover:scale-105"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
