import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const DashboardChoicePage = () => {
  const [databases, setDatabases] = useState([]);
  const [selectedDb, setSelectedDb] = useState('');
  const [error, setError] = useState('');
  const [isDbDashboard, setIsDbDashboard] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDatabases = async () => {
      try {
        const dbCredentials = JSON.parse(localStorage.getItem('dbCredentials'));
        if (!dbCredentials) {
          setError('No credentials found. Please log in again.');
          return;
        }

        const response = await axios.get('http://localhost:5000/api/databases', {
          params: {
            host: dbCredentials.host,
            username: dbCredentials.username,
            password: dbCredentials.password,
          },
        });

        if (response.data && response.data.databases) {
          setDatabases(response.data.databases);
        } else {
          setError('No databases found.');
        }
      } catch (error) {
        console.error('Error fetching databases:', error);
        setError('Failed to load databases.');
      }
    };

    if (isDbDashboard) {
      fetchDatabases();
    }
  }, [isDbDashboard]);

  const handleChoice = (choice) => {
    if (choice === 'sgbd') {
      navigate('/sgbd-overview'); // Navigate to the SGBD Dashboard
    } else if (choice === 'database') {
      setIsDbDashboard(true); // Show database selection
    }
  };

  const handleSubmitDatabase = () => {
    if (selectedDb) {
      // Store the selected database in localStorage
      localStorage.setItem('selectedDb', selectedDb);
      // Proceed to MainPage with the selected database
      navigate('/main'); // MainPage is your "Database Dashboard"
    } else {
      setError('Please select a database.');
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-900 text-white">
      <div className="text-center">
        <h2 className="text-3xl font-bold mb-6">Choose Your Dashboard</h2>
        <p className="text-lg text-gray-400 mb-6">
          Select the dashboard you want to explore or choose a database for more detailed insights.
        </p>
  
        {!isDbDashboard ? (
          <>
            <div className="mb-4">
              <button
                className="bg-gray-800 px-6 py-3 mb-4 text-white rounded-lg w-full hover:bg-gray-700 transition duration-200"
                onClick={() => handleChoice('sgbd')}
              >
                SGBD Dashboard
              </button>
            </div>
            <div>
              <button
                className="bg-gray-800 px-6 py-3 text-white rounded-lg w-full hover:bg-gray-700 transition duration-200"
                onClick={() => handleChoice('database')}
              >
                Database Dashboard
              </button>
            </div>
          </>
        ) : (
          <>
            <h3 className="text-xl font-semibold mb-4">Select a Database</h3>
            {error && <p className="text-red-500 mb-4">{error}</p>}
            {databases.length > 0 ? (
              <>
                <select
                  className="w-full p-3 bg-gray-800 text-white rounded-lg mb-6"
                  value={selectedDb}
                  onChange={(e) => setSelectedDb(e.target.value)}
                >
                  <option value="">Select a database</option>
                  {databases.map((db, index) => (
                    <option key={index} value={db}>
                      {db}
                    </option>
                  ))}
                </select>
                <button
                  onClick={handleSubmitDatabase}
                  className="bg-gray-800 px-6 py-3 text-white rounded-lg w-full hover:bg-gray-700 transition duration-200"
                >
                  Go to Database Dashboard
                </button>
              </>
            ) : (
              <p>No databases available.</p>
            )}
          </>
        )}
      </div>
    </div>
  );
  
  
  
  
};

export default DashboardChoicePage;
