import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';

const MainPage = ({ isSidebarOpen, toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({
    barChart: { /* ... */ },
    lineChart: { /* ... */ },
    pieChart: { /* ... */ },
    areaChart: { /* ... */ },
    radarChart: { /* ... */ },
    heatmapChart: { /* ... */ },
    scatterChart: { /* ... */ },
  });

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
    if (credentials) {
      const { host, username, password, database } = credentials;

      const fetchData = () => {
        fetch(`http://localhost:5000/api/database-metrics?host=${host}&username=${username}&password=${password}&database=${database}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success && data.metrics.length > 0) {
              const metrics = data.metrics;
              setData(metrics);
              setChartData({ /* Updated based on fetched data */ });
            }
          })
          .catch((err) => console.error('Error fetching data:', err));
      };

      fetchData();
      const intervalId = setInterval(fetchData, 5000);
      return () => clearInterval(intervalId);
    }
  }, [chartData]);

  return (
    <div className={`main-page min-h-screen transition-all duration-300 ${isSidebarOpen ? 'ml-64' : 'ml-16'} bg-gradient-to-b from-gray-800 via-gray-900 to-black`}>
      <button
        onClick={toggleSidebar}
        className="fixed top-4 left-4 z-50 bg-gradient-to-r from-purple-500 to-indigo-600 text-white p-3 rounded-full shadow-lg hover:scale-105 transition-transform duration-300"
      >
        {isSidebarOpen ? 'Close' : 'Open'} Sidebar
      </button>

      <div className="py-10 px-6">
        <h2 className="text-4xl font-extrabold text-white mb-8">Database Monitoring Dashboard</h2>
        
        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {data.map((item, index) => (
            <motion.div
              key={index}
              className="bg-gradient-to-r from-purple-600 via-indigo-600 to-blue-600 p-6 rounded-xl shadow-lg transform hover:scale-105 transition-transform duration-300"
            >
              <h3 className="text-lg font-bold text-white">{item.name}</h3>
              <p className="text-gray-200">{item.value}</p>
            </motion.div>
          ))}
        </motion.div>

        <div className="mt-12 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 xl:grid-cols-3 gap-8">
          {/* Bar Chart */}
          <div>
            <h3 className="text-2xl font-bold text-white mb-4">Database Performance Metrics</h3>
            <Chart
              options={chartData.barChart.options}
              series={chartData.barChart.series}
              type="bar"
              height={350}
            />
          </div>

          {/* Line Chart */}
          <div>
            <h3 className="text-2xl font-bold text-white mb-4">Performance Over Time</h3>
            <Chart
              options={chartData.lineChart.options}
              series={chartData.lineChart.series}
              type="line"
              height={350}
            />
          </div>

          {/* Pie Chart */}
          <div>
            <h3 className="text-2xl font-bold text-white mb-4">Metrics Distribution</h3>
            <Chart
              options={chartData.pieChart.options}
              series={chartData.pieChart.series}
              type="pie"
              height={350}
            />
          </div>

          {/* Additional Charts */}
          {/* ... */}
        </div>
      </div>
    </div>
  );
};

export default MainPage;
