import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import LoginPage from './components/LoginPage';
import MainPage from './components/MainPage';
import SgbdOverview from './components/SgbdOverview';
import DashboardChoicePage from './components/DashboardChoicePage';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to handle successful login
  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
  };

  return (
    <Router>
      <div className="flex flex-col h-screen">
        <Navbar />
        <div className="flex-1 overflow-y-auto">
          <Routes>
            <Route path="/" element={<MainPage />} />
            <Route path="/login" element={<LoginPage onLoginSuccess={handleLoginSuccess} />} />
            <Route path="/dashboard-choice" element={<DashboardChoicePage />} />
            <Route path="/sgbd-overview" element={<SgbdOverview />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
