import React, { useEffect } from 'react';
import { motion } from 'framer-motion';

const DemoPage = () => {
  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('.fadeIn');
      const scrollPosition = window.scrollY + window.innerHeight;

      sections.forEach(section => {
        if (section.offsetTop < scrollPosition) {
          section.classList.add('opacity-100');
          section.classList.remove('opacity-0');
        } else {
          section.classList.remove('opacity-100');
          section.classList.add('opacity-0');
        }
      });

      // Update the progress line
      const scrollHeight = document.documentElement.scrollHeight - window.innerHeight;
      const scrollProgress = (window.scrollY / scrollHeight) * 100;
      document.querySelector('.scrollProgressLine').style.height = `${scrollProgress}%`;
    };

    // Add the scroll event listener
    window.addEventListener('scroll', handleScroll);

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="bg-gray-900 text-white min-h-screen flex flex-col">
      {/* Header Section */}
      <header className="bg-gradient-to-r from-purple-600 to-blue-500 py-20 text-center">
        <motion.h1 
          className="text-4xl font-extrabold mb-4 opacity-0 fadeIn"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          Welcome to DBVision
        </motion.h1>
        <motion.p
          className="text-lg max-w-2xl mx-auto opacity-0 fadeIn"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.7 }}
        >
          Monitor your database with interactive charts and real-time data visualization. Let's take a quick tour of what DBVision can do!
        </motion.p>
      </header>

      {/* Features Section */}
      <section className="relative py-12 px-4 sm:px-6 lg:px-8">
        {/* The Progress Line */}
        <div className="fixed top-0 right-0 h-0 w-1 bg-gray-300 z-50 scrollProgressLine transition-all duration-300 shadow-lg shadow-purple-500"></div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {/* Section 1 */}
          <div className="bg-gray-800 rounded-lg shadow-lg p-6 opacity-0 transform transition-opacity duration-1000 fadeIn">
            <h3 className="text-2xl font-semibold text-purple-500 mb-4">Interactive Dashboards</h3>
            <p className="text-gray-300">
              Visualize your database data with real-time interactive charts that update as your database changes.
            </p>
          </div>

          {/* Section 2 */}
          <div className="bg-gray-800 rounded-lg shadow-lg p-6 opacity-0 transform transition-opacity duration-1000 fadeIn">
            <h3 className="text-2xl font-semibold text-purple-500 mb-4">Manage Your Data</h3>
            <p className="text-gray-300">
              Easily manage and analyze your database with intuitive controls that allow you to filter, query, and modify your data.
            </p>
          </div>

          {/* Section 3 */}
          <div className="bg-gray-800 rounded-lg shadow-lg p-6 opacity-0 transform transition-opacity duration-1000 fadeIn">
            <h3 className="text-2xl font-semibold text-purple-500 mb-4">Real-Time Updates</h3>
            <p className="text-gray-300">
              Stay up to date with your databaseâ€™s performance and changes as the dashboard updates live.
            </p>
          </div>
        </div>
      </section>

      {/* Call-to-Action Section */}
      <section className="flex justify-center py-8">
        <button
          className="bg-blue-600 hover:bg-blue-700 text-white text-xl font-semibold py-3 px-8 rounded-full shadow-lg transform hover:scale-105 transition-transform duration-300"
          onClick={() => window.location.href = '/login'}
        >
          Connect Your Database
        </button>
      </section>

      {/* Footer Section */}
      <footer className="bg-gray-800 py-4 text-center">
        <p className="text-gray-400">
          DBVision - All Rights Reserved | &copy; 2024
        </p>
      </footer>
    </div>
  );
};

export default DemoPage;
