import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';

const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({
    barChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          mode: 'dark',
        },
      },
    },
    lineChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          mode: 'dark',
        },
      },
    },
    pieChart: {
      series: [],
      options: {
        labels: [],
        theme: {
          mode: 'dark',
        },
      },
    },
    heatmapChart: {
      series: [],
      options: {
        chart: {
          type: 'heatmap',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          mode: 'dark',
        },
      },
    },
    multiSeriesLineChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          mode: 'dark',
        },
      },
    },
  });

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
    if (credentials) {
      const { host, username, password, database } = credentials;

      const fetchData = () => {
        fetch(`http://localhost:5000/api/database-metrics?host=${host}&username=${username}&password=${password}&database=${database}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success && data.metrics.length > 0) {
              const metrics = data.metrics;
              setData(metrics);
              setChartData({
                barChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.barChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                lineChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.lineChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                pieChart: {
                  series: metrics.map((item) => item.value),
                  options: {
                    ...chartData.pieChart.options,
                    labels: metrics.map((item) => item.name),
                  },
                },
                heatmapChart: {
                  series: metrics.map((item) => ({
                    name: item.name,
                    data: metrics.map((m) => Math.floor(Math.random() * 100)), // Example heatmap data
                  })),
                  options: {
                    ...chartData.heatmapChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                multiSeriesLineChart: {
                  series: metrics.map((item, index) => ({
                    name: `Metric ${index + 1}`,
                    data: metrics.map((m) => Math.floor(Math.random() * 100)), // Example multi-series data
                  })),
                  options: {
                    ...chartData.multiSeriesLineChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
              });
            }
          })
          .catch((err) => console.error('Error fetching data:', err));
      };

      fetchData(); // Initial fetch
      const intervalId = setInterval(fetchData, 5000); // Poll every 5 seconds

      return () => clearInterval(intervalId); // Cleanup on component unmount
    }
  }, [chartData]);

  return (
    <div className="main-page bg-gray-900 min-h-screen p-4">
      <button
        onClick={toggleSidebar}
        className="bg-purple-600 text-white p-2 rounded-md hover:bg-purple-700 transition duration-300 transform hover:scale-105 mb-6"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-4">
        <h2 className="text-3xl font-bold text-white mb-6">Database Monitoring Dashboard</h2>

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {data.map((item, index) => (
            <div
              key={index}
              className="bg-gray-800 p-6 rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-300"
            >
              <h3 className="text-xl font-semibold text-purple-500">{item.name}</h3>
              <p className="text-gray-300">{item.value}</p>
            </div>
          ))}
        </motion.div>

        <div className="mt-12">
          <h3 className="text-2xl font-semibold text-white mb-4">Database Performance Metrics</h3>
          <Chart
            options={chartData.barChart.options}
            series={chartData.barChart.series}
            type="bar"
            height={350}
          />
        </div>

        <div className="mt-12">
          <h3 className="text-2xl font-semibold text-white mb-4">Database Performance Over Time</h3>
          <Chart
            options={chartData.lineChart.options}
            series={chartData.lineChart.series}
            type="line"
            height={350}
          />
        </div>

        <div className="mt-12">
          <h3 className="text-2xl font-semibold text-white mb-4">Database Metrics Distribution</h3>
          <Chart
            options={chartData.pieChart.options}
            series={chartData.pieChart.series}
            type="pie"
            height={350}
          />
        </div>

        <div className="mt-12">
          <h3 className="text-2xl font-semibold text-white mb-4">Database Activity Heatmap</h3>
          <Chart
            options={chartData.heatmapChart.options}
            series={chartData.heatmapChart.series}
            type="heatmap"
            height={350}
          />
        </div>

        <div className="mt-12">
          <h3 className="text-2xl font-semibold text-white mb-4">Multi-Series Metrics Comparison</h3>
          <Chart
            options={chartData.multiSeriesLineChart.options}
            series={chartData.multiSeriesLineChart.series}
            type="line"
            height={350}
          />
        </div>
      </div>
    </div>
  );
};

export default MainPage;
