import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';

const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [performanceMetrics, setPerformanceMetrics] = useState(null); // New state for performance metrics
  const [chartData, setChartData] = useState({
    barChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    lineChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    pieChart: {
      series: [],
      options: {
        labels: [],
        theme: {
          palette: 'palette5',
        },
      },
    },
    areaChart: {
      series: [],
      options: {
        chart: {
          type: 'area',
          stacked: true,
          toolbar: { show: false },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    radarChart: {
      series: [],
      options: {
        chart: {
          type: 'radar',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    heatmapChart: {
      series: [],
      options: {
        chart: {
          type: 'heatmap',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    scatterChart: {
      series: [],
      options: {
        chart: {
          type: 'scatter',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
  });

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
    if (credentials) {
      const { host, username, password, database } = credentials;

      const fetchData = () => {
        fetch(`http://localhost:5000/api/database-metrics?host=${host}&username=${username}&password=${password}&database=${database}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success && data.metrics.length > 0) {
              const metrics = data.metrics;
              setData(metrics);
              setChartData({
                barChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.barChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                lineChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.lineChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                pieChart: {
                  series: metrics.map((item) => item.value),
                  options: {
                    ...chartData.pieChart.options,
                    labels: metrics.map((item) => item.name),
                  },
                },
                areaChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.areaChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                radarChart: {
                  series: [{ name: 'Metrics', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.radarChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                heatmapChart: {
                  series: metrics.map((item, index) => ({ name: item.name, data: [item.value, item.value * 0.9, item.value * 1.1] })),
                  options: {
                    ...chartData.heatmapChart.options,
                    xaxis: { categories: ['Metric 1', 'Metric 2', 'Metric 3'] },
                  },
                },
                scatterChart: {
                  series: metrics.map((item, index) => ({ name: item.name, data: [[index, item.value]] })),
                  options: {
                    ...chartData.scatterChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
              });
            }
          })
          .catch((err) => console.error('Error fetching data:', err));
      };

      const fetchPerformanceMetrics = () => {
        fetch(`http://localhost:5000/api/performance-metrics?host=${host}&username=${username}&password=${password}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success) {
              setPerformanceMetrics(data);
            }
          })
          .catch((err) => console.error('Error fetching performance metrics:', err));
      };

      fetchData(); // Initial fetch
      fetchPerformanceMetrics(); // Initial fetch for performance metrics
      const intervalId = setInterval(() => {
        fetchData();
        fetchPerformanceMetrics();
      }, 5000); // Poll every 5 seconds

      return () => clearInterval(intervalId); // Cleanup on component unmount
    }
  }, [chartData]);

  return (
    <div className="main-page bg-gray-900 min-h-screen p-8 font-sans space-y-12">
      {/* Sidebar Toggle Button */}
      <button
        onClick={toggleSidebar}
        className="bg-gray-800 text-white p-4 rounded-lg hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-purple-600 transition-all duration-300 transform hover:scale-105"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            d="M4 6h16M4 12h16M4 18h16"
          />
        </svg>
      </button>
  
      {/* Performance Metrics */}
      {performanceMetrics && (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl transform hover:scale-105 transition-all duration-300">
            <h3 className="text-2xl font-semibold text-white mb-4">CPU Load</h3>
            <p className="text-sm text-gray-400 mb-6">
              Current CPU load over the last minute.
            </p>
            <p className="text-lg text-white">
              {performanceMetrics.system.cpuLoad.toFixed(2)}%
            </p>
          </div>
          <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl transform hover:scale-105 transition-all duration-300">
            <h3 className="text-2xl font-semibold text-white mb-4">Memory Usage</h3>
            <p className="text-sm text-gray-400 mb-6">
              Current memory usage.
            </p>
            <p className="text-lg text-white">
              {((performanceMetrics.system.memoryUsage.used / performanceMetrics.system.memoryUsage.total) * 100).toFixed(2)}%
            </p>
          </div>
        </div>
      )}
  
      {/* Dashboard Title */}
      <div className="text-center">
        <h2 className="text-4xl font-extrabold text-white tracking-tight">
          Real-Time Database Monitoring Dashboard
        </h2>
        <p className="text-lg text-gray-400 mt-4">
          Monitor your database performance, track key metrics, and ensure smooth operation with real-time insights.
        </p>
      </div>
  
      {/* Dashboard Grid */}
      <motion.div
        className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.7 }}
      >
        {data.map((item, index) => (
          <div
            key={index}
            className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-2xl transform hover:scale-105 transition-all duration-300"
          >
            <h3 className="text-2xl font-semibold text-purple-500">{item.name}</h3>
            <p className="text-lg text-gray-300">{item.value}</p>
          </div>
        ))}
      </motion.div>
  
      {/* Charts Section */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {/* Database Performance */}
        <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl transform hover:scale-105 transition-all duration-300">
          <h3 className="text-2xl font-semibold text-white mb-4">Performance Metrics</h3>
          <p className="text-sm text-gray-400 mb-6">
            Track the overall performance of your database in real time.
          </p>
          <Chart
            options={chartData.barChart.options}
            series={chartData.barChart.series}
            type="bar"
            height={250}
          />
        </div>
  
        {/* Trends Over Time */}
        <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl transform hover:scale-105 transition-all duration-300">
          <h3 className="text-2xl font-semibold text-white mb-4">Trends Over Time</h3>
          <p className="text-sm text-gray-400 mb-6">
            Visualize performance trends over time.
          </p>
          <Chart
            options={chartData.lineChart.options}
            series={chartData.lineChart.series}
            type="line"
            height={250}
          />
        </div>
  
        {/* Metrics Distribution */}
        <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl transform hover:scale-105 transition-all duration-300">
          <h3 className="text-2xl font-semibold text-white mb-4">Metrics Distribution</h3>
          <p className="text-sm text-gray-400 mb-6">
            View the distribution of key metrics.
          </p>
          <Chart
            options={chartData.pieChart.options}
            series={chartData.pieChart.series}
            type="pie"
            height={250}
          />
        </div>
      </div>
    </div>
  );
  
  
};

export default MainPage;
