import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(true);
  const [notifications, setNotifications] = useState([]);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    const socket = new WebSocket('ws://localhost:8080');

    socket.onopen = () => {
      console.log('Connecté au serveur WebSocket');
    };

    socket.onmessage = (event) => {
      const notification = JSON.parse(event.data);
      console.log('Notification reçue:', notification);
      setNotifications((prevNotifications) => [notification, ...prevNotifications]);
    };

    socket.onclose = () => {
      console.log('Déconnecté du serveur WebSocket');
    };

    return () => {
      socket.close();
    };
  }, []);

  return (
    <aside
      className={`bg-gray-900 text-white p-6 shadow-md transform transition-transform duration-300 ease-in-out ${
        isOpen ? 'w-64' : 'w-20'
      }`}
    >
      <button
        onClick={toggleSidebar}
        className="text-white mb-6"
      >
        {isOpen ? 'Close' : 'Open'}
      </button>
      {isOpen && (
        <div>
          <h2 className="text-xl font-bold mb-6">Navigation</h2>
          <ul className="space-y-6">
            <li>
              <NavLink
                to="/main"
                activeClassName="text-blue-400"
                className="hover:text-blue-400 transition-colors"
              >
                Dashboard
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/data-visualization"
                activeClassName="text-blue-400"
                className="hover:text-blue-400 transition-colors"
              >
                Data Visualization
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/manage-data"
                activeClassName="text-blue-400"
                className="hover:text-blue-400 transition-colors"
              >
                Manage Data
              </NavLink>
            </li>
          </ul>
          
          <h2 className="text-xl font-bold mt-6 mb-4">Notifications</h2>
          <ul className="space-y-4 overflow-y-auto max-h-64">
            {notifications.map((notification, index) => (
              <li
                key={index}
                className="bg-gray-800 p-4 rounded-lg shadow-md transition-all duration-300 hover:bg-gray-700"
              >
                {notification.message}
              </li>
            ))}
          </ul>
        </div>
      )}
    </aside>
  );
};

export default Sidebar;
