import React, { useEffect } from 'react';
import debounce from 'lodash.debounce';

const DemoPage = () => {
  useEffect(() => {
    const handleScroll = debounce(() => {
      const sections = document.querySelectorAll('.fadeIn');
      const scrollPosition = window.scrollY + window.innerHeight;

      sections.forEach((section, index) => {
        const isVisible = section.offsetTop < scrollPosition - 100;

        if (isVisible) {
          section.classList.add('opacity-100');
          section.classList.add(index % 2 === 0 ? 'animate-bottom-to-top' : 'animate-top-to-bottom');
        } else {
          section.classList.remove('opacity-100');
          section.classList.remove('animate-bottom-to-top', 'animate-top-to-bottom');
        }
      });
    }, 100);

    // Add scroll event listener
    window.addEventListener('scroll', handleScroll);

    // Cleanup
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="bg-gray-900 text-white min-h-screen">
      {/* Header Section */}
      <header className="py-20 text-center">
        <h1 className="text-4xl font-extrabold mb-4 text-purple-400">Welcome to DBVision</h1>
        <p className="text-lg max-w-2xl mx-auto text-gray-300">
          Monitor your database with interactive charts and real-time data visualization. Let's take a quick tour of what DBVision can do!
        </p>
      </header>

      {/* Features Section */}
      <section className="relative py-12 px-4 sm:px-6 lg:px-8 space-y-12">
        {/* The Progress Line */}
        <div className="fixed top-0 right-0 h-0 w-1 bg-purple-500 z-50 scrollProgressLine transition-all duration-300"></div>

        {/* Section 1 */}
        <div className="bg-gray-800 rounded-lg shadow-lg p-6 opacity-0 fadeIn transform transition-opacity duration-1000">
          <h3 className="text-2xl font-semibold text-purple-400 mb-4">Interactive Dashboards</h3>
          <p className="text-gray-300">
            Visualize your database data with real-time interactive charts that update as your database changes.
          </p>
        </div>

        {/* Section 2 */}
        <div className="bg-gray-800 rounded-lg shadow-lg p-6 opacity-0 fadeIn transform transition-opacity duration-1000">
          <h3 className="text-2xl font-semibold text-purple-400 mb-4">Manage Your Data</h3>
          <p className="text-gray-300">
            Easily manage and analyze your database with intuitive controls that allow you to filter, query, and modify your data.
          </p>
        </div>

        {/* Section 3 */}
        <div className="bg-gray-800 rounded-lg shadow-lg p-6 opacity-0 fadeIn transform transition-opacity duration-1000">
          <h3 className="text-2xl font-semibold text-purple-400 mb-4">Real-Time Updates</h3>
          <p className="text-gray-300">
            Stay up to date with your database’s performance and changes as the dashboard updates live.
          </p>
        </div>
      </section>

      {/* Call-to-Action Section */}
      <section className="flex justify-center py-8">
        <button
          className="bg-purple-600 hover:bg-purple-700 text-white text-xl font-semibold py-3 px-8 rounded-full shadow-lg transform hover:scale-105 transition-transform duration-300"
          onClick={() => window.location.href = '/login'}
        >
          Connect Your Database
        </button>
      </section>

      {/* Footer Section */}
      <footer className="py-4 text-center">
        <p className="text-gray-400">
          DBVision - All Rights Reserved | &copy; 2024
        </p>
      </footer>
    </div>
  );
};

export default DemoPage;
