import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import DemoPage from './components/DemoPage'; // Import the DemoPage component
import LoginPage from './components'; // Import the LoginPage component
import MainPage from './components/MainPage'; // Import the MainPage component
import Navbar from './components/Navbar'; // Import Navbar (optional)
import Sidebar from './components/Sidebar'; // Import Sidebar (optional)

const App = () => {
  return (
    <Router>
      <div className="App">
        {/* Optionally add Navbar here */}
        <Navbar />

        {/* Sidebar can be added if you want it to be present on all pages */}
        {/* <Sidebar /> */}

        {/* Main Content */}
        <Switch>
          {/* Route to demo page (landing page) */}
          <Route exact path="/" component={DemoPage} />

          {/* Route to login page */}
          <Route path="/login" component={LoginPage} />

          {/* Route to the main page after successful login */}
          <Route path="/main" component={MainPage} />
        </Switch>
      </div>
    </Router>
  );
};

export default App;
