      setData([
        { name: 'Users', value: 150 },
        { name: 'Orders', value: 1200 },
        { name: 'Products', value: 800 },
      ]);
    }
  }, []);

  return (
    <div className="main-page p-4">
      <button
        onClick={toggleSidebar}
        className="bg-purple-600 text-white p-2 rounded-md hover:bg-purple-700 transition"
      >
        Toggle Sidebar
      </button>
