import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SgbdMetricsChart from './SgbdMetricsChart'; // Import the chart component

const SgbdOverview = () => {
  const [performanceMetrics, setPerformanceMetrics] = useState(null);
  const [diskIO, setDiskIO] = useState([]);
  const [networkTraffic, setNetworkTraffic] = useState([]);
  const [innodbMetrics, setInnodbMetrics] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
      if (credentials) {
        const { host, username, password } = credentials;

        try {
          const performanceResponse = await axios.get('http://localhost:5000/api/performance-metrics', {
            params: { host, username, password },
          });
          if (performanceResponse.data.success) {
            setPerformanceMetrics(performanceResponse.data);
          }

          const diskIOResponse = await axios.get('http://localhost:5000/api/disk-io', {
            params: { host, username, password },
          });
          if (diskIOResponse.data.success) {
            setDiskIO(diskIOResponse.data.diskIO);
          }

          const networkTrafficResponse = await axios.get('http://localhost:5000/api/network-traffic', {
            params: { host, username, password },
          });
          if (networkTrafficResponse.data.success) {
            setNetworkTraffic(networkTrafficResponse.data.networkTraffic);
          }

          const innodbMetricsResponse = await axios.get('http://localhost:5000/api/innodb-metrics', {
            params: { host, username, password },
          });
          if (innodbMetricsResponse.data.success) {
            setInnodbMetrics(innodbMetricsResponse.data.innodbMetrics);
          }
        } catch (error) {
          console.error('Error fetching SGBD data:', error);
        } finally {
          setLoading(false);
        }
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>SGBD Overview</h1>
      {performanceMetrics ? (
        <div>
          {/* Display SGBD Data */}
          <p><strong>Threads Connected:</strong> {performanceMetrics.database.threadsConnected}</p>
          <p><strong>Total Connections:</strong> {performanceMetrics.database.totalConnections}</p>
          <p><strong>Uptime:</strong> {performanceMetrics.database.uptimeSeconds} seconds</p>
          <p><strong>Memory Usage:</strong> {((performanceMetrics.system.memoryUsage.used / performanceMetrics.system.memoryUsage.total) * 100).toFixed(2)}%</p>
          <p><strong>CPU Load:</strong> {performanceMetrics.system.cpuLoad}%</p>
          
          {/* Add the chart below the SGBD data */}
          {performanceMetrics && <SgbdMetricsChart data={performanceMetrics} />}
        </div>
      ) : (
        <p>Failed to load data</p>
      )}
    </div>
  );
};

export default SgbdOverview;
