import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';

const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({
    barChart: { series: [], options: { chart: { toolbar: { show: false }, animations: { enabled: true, easing: 'easeinout', speed: 800 }, }, xaxis: { categories: [], }, theme: { palette: 'palette5', }, }, },
    lineChart: { series: [], options: { chart: { toolbar: { show: false }, animations: { enabled: true, easing: 'easeinout', speed: 800 }, }, xaxis: { categories: [], }, theme: { palette: 'palette5', }, }, },
    pieChart: { series: [], options: { labels: [], theme: { palette: 'palette5', }, }, },
    areaChart: { series: [], options: { chart: { type: 'area', stacked: true, toolbar: { show: false }, }, xaxis: { categories: [], }, theme: { palette: 'palette5', }, }, },
    radarChart: { series: [], options: { chart: { type: 'radar', }, xaxis: { categories: [], }, theme: { palette: 'palette5', }, }, },
    heatmapChart: { series: [], options: { chart: { type: 'heatmap', }, xaxis: { categories: [], }, theme: { palette: 'palette5', }, }, },
    scatterChart: { series: [], options: { chart: { type: 'scatter', }, xaxis: { categories: [], }, theme: { palette: 'palette5', }, }, },
    cpuUsageChart: { series: [], options: { chart: { type: 'radialBar', }, labels: ['CPU Usage'], theme: { palette: 'palette5', }, }, },
    memoryUsageChart: { series: [], options: { chart: { type: 'radialBar', }, labels: ['Memory Usage'], theme: { palette: 'palette5', }, }, },
    responseTimeChart: { series: [], options: { chart: { type: 'line', }, xaxis: { categories: [], }, theme: { palette: 'palette5', }, }, },
    activeConnectionsChart: { series: [], options: { chart: { type: 'bar', }, xaxis: { categories: [], }, theme: { palette: 'palette5', }, }, },
    diskUsageChart: { series: [], options: { chart: { type: 'donut', }, labels: ['Disk Usage'], theme: { palette: 'palette5', }, }, },
    errorRateChart: { series: [], options: { chart: { type: 'pie', }, labels: ['Error Rate'], theme: { palette: 'palette5', }, }, },
    threadUsageChart: { series: [], options: { chart: { type: 'radialBar', }, labels: ['Thread Usage'], theme: { palette: 'palette5', }, }, },
    ioOperationsChart: { series: [], options: { chart: { type: 'line', }, xaxis: { categories: [], }, theme: { palette: 'palette5', }, }, },
    userSessionsChart: { series: [], options: { chart: { type: 'bar', }, xaxis: { categories: [], }, theme: { palette: 'palette5', }, }, },
  });

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
    if (credentials) {
      const { host, username, password, database } = credentials;

      const fetchData = () => {
        fetch(`http://localhost:5000/api/database-metrics?host=${host}&username=${username}&password=${password}&database=${database}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success) {
              const metrics = data.metrics;
              const additionalMetrics = data.additionalMetrics;

              setData(metrics);
              setChartData({
                barChart: { series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }], options: { ...chartData.barChart.options, xaxis: { categories: metrics.map((item) => item.name) } }, },
                lineChart: { series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }], options: { ...chartData.lineChart.options, xaxis: { categories: metrics.map((item) => item.name) } }, },
                cpuUsageChart: { series: [additionalMetrics.cpuUsage], options: { ...chartData.cpuUsageChart.options } },
                memoryUsageChart: { series: [additionalMetrics.memoryUsage], options: { ...chartData.memoryUsageChart.options } },
                responseTimeChart: { series: [{ name: 'Response Time', data: additionalMetrics.responseTimes }], options: { ...chartData.responseTimeChart.options, xaxis: { categories: ['Query 1', 'Query 2', 'Query 3'] } } },
                activeConnectionsChart: { series: [{ name: 'Active Connections', data: [additionalMetrics.activeConnections] }], options: { ...chartData.activeConnectionsChart.options } },
                diskUsageChart: { series: [additionalMetrics.diskUsage], options: { ...chartData.diskUsageChart.options } },
                errorRateChart: { series: [additionalMetrics.errorRate], options: { ...chartData.errorRateChart.options } },
                threadUsageChart: { series: [additionalMetrics.threadUsage], options: { ...chartData.threadUsageChart.options } },
                ioOperationsChart: { series: [{ name: 'I/O Operations', data: [additionalMetrics.ioOperations] }], options: { ...chartData.ioOperationsChart.options } },
                userSessionsChart: { series: [{ name: 'User Sessions', data: [additionalMetrics.userSessions] }], options: { ...chartData.userSessionsChart.options } },
              });
            }
          })
          .catch((err) => console.error('Error fetching data:', err));
      };

      fetchData(); // Initial fetch
      const intervalId = setInterval(fetchData, 5000); // Poll every 5 seconds

      return () => clearInterval(intervalId); // Cleanup on component unmount
    }
  }, [chartData]);

  return (
    <div className="main-page bg-gray-900 min-h-screen p-4">
      <button
        onClick={toggleSidebar}
        className="bg-purple-600 text-white p-2 rounded-md hover:bg-purple-700 transition duration-300 transform hover:scale-105 mb-6"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-4">
        <h2 className="text-3xl font-bold text-white mb-6">Database Monitoring Dashboard</h2>

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {data.map((item, index) => (
            <div
              key={index}
              className="bg-gray-800 p-6 rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-300"
            >
              <h3 className="text-xl font-semibold text-purple-500">{item.name}</h3>
              <p className="text-white text-lg mt-2">Rows: {item.value}</p>
            </div>
          ))}
        </motion.div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mt-8">
          {/* Render the updated charts */}
          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">CPU Usage</h3>
            <Chart options={chartData.cpuUsageChart.options} series={chartData.cpuUsageChart.series} type="radialBar" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Memory Usage</h3>
            <Chart options={chartData.memoryUsageChart.options} series={chartData.memoryUsageChart.series} type="radialBar" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Response Times</h3>
            <Chart options={chartData.responseTimeChart.options} series={chartData.responseTimeChart.series} type="line" height={350} />
          </div>

          {/* Additional charts for other metrics */}
          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Active Connections</h3>
            <Chart options={chartData.activeConnectionsChart.options} series={chartData.activeConnectionsChart.series} type="bar" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Disk Usage</h3>
            <Chart options={chartData.diskUsageChart.options} series={chartData.diskUsageChart.series} type="donut" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Error Rate</h3>
            <Chart options={chartData.errorRateChart.options} series={chartData.errorRateChart.series} type="pie" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">Thread Usage</h3>
            <Chart options={chartData.threadUsageChart.options} series={chartData.threadUsageChart.series} type="radialBar" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">I/O Operations</h3>
            <Chart options={chartData.ioOperationsChart.options} series={chartData.ioOperationsChart.series} type="line" height={350} />
          </div>

          <div className="col-span-1">
            <h3 className="text-xl font-semibold text-white">User Sessions</h3>
            <Chart options={chartData.userSessionsChart.options} series={chartData.userSessionsChart.series} type="bar" height={350} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
