import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LoginPage = ({ onLoginSuccess }) => {
  const [host, setHost] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [database, setDatabase] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
  
    if (!host || !username || !password || !database) {
      setError('All fields are required.');
      return;
    }
  
    try {
      const response = await fetch('http://localhost:5000/connect', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ host, username, password, database }),
      });
  
      const data = await response.json();
      if (data.success) {
        localStorage.setItem('dbCredentials', JSON.stringify({ host, username, password, database }));
        onLoginSuccess();
        navigate('/main');
      } else {
        setError(data.message);
      }
    } catch (error) {
      setError('Failed to connect to the database. Please check your credentials.');
    }
  };

  return (
    <div className="bg-gray-900 min-h-screen flex items-center justify-center px-4 py-8">
      <div className="bg-gray-800 rounded-lg shadow-lg p-8 sm:w-96 w-full">
        <h2 className="text-3xl font-extrabold text-center text-white mb-6">DBVision Login</h2>
        {error && <p className="text-red-500 text-center mb-4">{error}</p>}
        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label htmlFor="host" className="text-gray-300 font-semibold">Host:</label>
            <input
              type="text"
              id="host"
              value={host}
              onChange={(e) => setHost(e.target.value)}
              className="w-full mt-2 p-3 rounded-md bg-gray-700 text-white border border-gray-600 focus:outline-none focus:ring-2 focus:ring-purple-600"
              placeholder="Enter host"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="username" className="text-gray-300 font-semibold">Username:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full mt-2 p-3 rounded-md bg-gray-700 text-white border border-gray-600 focus:outline-none focus:ring-2 focus:ring-purple-600"
              placeholder="Enter username"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="password" className="text-gray-300 font-semibold">Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full mt-2 p-3 rounded-md bg-gray-700 text-white border border-gray-600 focus:outline-none focus:ring-2 focus:ring-purple-600"
              placeholder="Enter password"
            />
          </div>
          <div className="mb-6">
            <label htmlFor="database" className="text-gray-300 font-semibold">Database:</label>
            <input
              type="text"
              id="database"
              value={database}
              onChange={(e) => setDatabase(e.target.value)}
              className="w-full mt-2 p-3 rounded-md bg-gray-700 text-white border border-gray-600 focus:outline-none focus:ring-2 focus:ring-purple-600"
              placeholder="Enter database"
            />
          </div>
          <button
            type="submit"
            className="w-full py-3 px-6 bg-purple-600 text-white rounded-md shadow-md hover:bg-purple-700 transition duration-300 transform hover:scale-105"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
