import React from 'react';

const PerformanceMetricCard = ({ title, description, value, gradient, percentage }) => {
  return (
    <div className={`bg-gradient-to-r ${gradient} p-6 rounded-lg shadow-lg transform hover:scale-105 transition-all duration-300`}>
      <h3 className="text-2xl font-semibold text-white mb-4">{title}</h3>
      <p className="text-sm text-gray-200 mb-6">{description}</p>
      <p className="text-lg text-white font-bold">{value}%</p>
      <div className="w-full bg-gray-700 h-2 mt-4 rounded-lg">
        <div
          className="h-2 rounded-lg"
          style={{
            width: `${percentage}%`,
            backgroundColor: percentage > 80 ? 'red' : 'green',
          }}
        ></div>
      </div>
    </div>
  );
};

export default PerformanceMetricCard;
