import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';

const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({
    barChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    lineChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    pieChart: {
      series: [],
      options: {
        labels: [],
        theme: {
          palette: 'palette5',
        },
      },
    },
    areaChart: {
      series: [],
      options: {
        chart: {
          type: 'area',
          stacked: true,
          toolbar: { show: false },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    radarChart: {
      series: [],
      options: {
        chart: {
          type: 'radar',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    heatmapChart: {
      series: [],
      options: {
        chart: {
          type: 'heatmap',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    scatterChart: {
      series: [],
      options: {
        chart: {
          type: 'scatter',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    // New metrics charts
    querySpeedChart: {
      series: [],
      options: {
        chart: {
          type: 'line',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    cpuUsageChart: {
      series: [],
      options: {
        chart: {
          type: 'line',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    memoryUsageChart: {
      series: [],
      options: {
        chart: {
          type: 'line',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    responseTimeChart: {
      series: [],
      options: {
        chart: {
          type: 'line',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    activeConnectionsChart: {
      series: [],
      options: {
        chart: {
          type: 'line',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
  });

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
    if (credentials) {
      const { host, username, password, database } = credentials;

      const fetchData = () => {
        fetch(`http://localhost:5000/api/database-metrics?host=${host}&username=${username}&password=${password}&database=${database}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success && data.metrics && data.metrics.length > 0) {
              const metrics = data.metrics;
              setData(metrics);
      
              // Ensure metrics arrays are defined
              const newQuerySpeedData = metrics.map((item) => item.querySpeed || 0);
              const newCpuUsageData = metrics.map((item) => item.cpuUsage || 0);
              const newMemoryUsageData = metrics.map((item) => item.memoryUsage || 0);
              const newResponseTimeData = metrics.map((item) => item.responseTime || 0);
              const newActiveConnectionsData = metrics.map((item) => item.activeConnections || 0);
      
              setChartData({
                querySpeedChart: {
                  series: [{ name: 'Query Speed', data: newQuerySpeedData }],
                  options: {
                    ...chartData.querySpeedChart.options,
                    xaxis: { categories: metrics.map((item) => item.name || 'Unknown') },
                  },
                },
                cpuUsageChart: {
                  series: [{ name: 'CPU Usage', data: newCpuUsageData }],
                  options: {
                    ...chartData.cpuUsageChart.options,
                    xaxis: { categories: metrics.map((item) => item.name || 'Unknown') },
                  },
                },
                memoryUsageChart: {
                  series: [{ name: 'Memory Usage', data: newMemoryUsageData }],
                  options: {
                    ...chartData.memoryUsageChart.options,
                    xaxis: { categories: metrics.map((item) => item.name || 'Unknown') },
                  },
                },
                responseTimeChart: {
                  series: [{ name: 'Response Time', data: newResponseTimeData }],
                  options: {
                    ...chartData.responseTimeChart.options,
                    xaxis: { categories: metrics.map((item) => item.name || 'Unknown') },
                  },
                },
                activeConnectionsChart: {
                  series: [{ name: 'Active Connections', data: newActiveConnectionsData }],
                  options: {
                    ...chartData.activeConnectionsChart.options,
                    xaxis: { categories: metrics.map((item) => item.name || 'Unknown') },
                  },
                },
              });
            } else {
              console.error('No valid metrics data available');
            }
          })
          .catch((err) => console.error('Error fetching data:', err));
      };
      

      fetchData(); // Initial fetch
      const intervalId = setInterval(fetchData, 5000); // Poll every 5 seconds

      return () => clearInterval(intervalId); // Cleanup on component unmount
    }
  }, [chartData]);

  return (
    <div className="main-page bg-gray-900 min-h-screen p-4">
      <button
        onClick={toggleSidebar}
        className="bg-purple-600 text-white p-2 rounded-md hover:bg-purple-700 transition duration-300 transform hover:scale-105 mb-6"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-4">
        <h2 className="text-3xl font-bold text-white mb-6">Database Monitoring Dashboard</h2>

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {data.map((item, index) => (
            <div
              key={index}
              className="bg-gray-800 p-6 rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-300"
            >
              <h3 className="text-xl font-semibold text-purple-500">{item.name}</h3>
              <p className="text-gray-300">{item.value}</p>
            </div>
          ))}
        </motion.div>

        <div className="mt-12 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 xl:grid-cols-3 gap-8">
          {/* Existing charts */}
          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Query Execution Speed</h3>
            <Chart
              options={chartData.querySpeedChart.options}
              series={chartData.querySpeedChart.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">CPU Usage</h3>
            <Chart
              options={chartData.cpuUsageChart.options}
              series={chartData.cpuUsageChart.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Memory Usage</h3>
            <Chart
              options={chartData.memoryUsageChart.options}
              series={chartData.memoryUsageChart.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Response Time</h3>
            <Chart
              options={chartData.responseTimeChart.options}
              series={chartData.responseTimeChart.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Active Connections</h3>
            <Chart
              options={chartData.activeConnectionsChart.options}
              series={chartData.activeConnectionsChart.series}
              type="line"
              height={350}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
