import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';
import axios from 'axios';

const MainPage = ({ toggleSidebar }) => {
  const [data, setData] = useState([]);
  const [chartData, setChartData] = useState({
    barChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    lineChart: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    pieChart: {
      series: [],
      options: {
        labels: [],
        theme: {
          palette: 'palette5',
        },
      },
    },
    areaChart: {
      series: [],
      options: {
        chart: {
          type: 'area',
          stacked: true,
          toolbar: { show: false },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    radarChart: {
      series: [],
      options: {
        chart: {
          type: 'radar',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    heatmapChart: {
      series: [],
      options: {
        chart: {
          type: 'heatmap',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    scatterChart: {
      series: [],
      options: {
        chart: {
          type: 'scatter',
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    queryExecutionSpeed: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    cpuUsage: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    memoryUsage: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    responseTime: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
    activeConnections: {
      series: [],
      options: {
        chart: {
          toolbar: { show: false },
          animations: { enabled: true, easing: 'easeinout', speed: 800 },
        },
        xaxis: {
          categories: [],
        },
        theme: {
          palette: 'palette5',
        },
      },
    },
  });

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
    if (credentials) {
      const { host, username, password, database } = credentials;

      const fetchData = () => {
        fetch(`http://localhost:5000/api/database-metrics?host=${host}&username=${username}&password=${password}&database=${database}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success && data.metrics.length > 0) {
              const metrics = data.metrics;
              const additionalMetrics = data.additionalMetrics;
              setData(metrics);
              setChartData({
                ...chartData,
                barChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.barChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                lineChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.lineChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                pieChart: {
                  series: metrics.map((item) => item.value),
                  options: {
                    ...chartData.pieChart.options,
                    labels: metrics.map((item) => item.name),
                  },
                },
                areaChart: {
                  series: [{ name: 'Row Count', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.areaChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                radarChart: {
                  series: [{ name: 'Metrics', data: metrics.map((item) => item.value) }],
                  options: {
                    ...chartData.radarChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                heatmapChart: {
                  series: metrics.map((item, index) => ({ name: item.name, data: [item.value, item.value * 0.9, item.value * 1.1] })),
                  options: {
                    ...chartData.heatmapChart.options,
                    xaxis: { categories: ['Metric 1', 'Metric 2', 'Metric 3'] },
                  },
                },
                scatterChart: {
                  series: metrics.map((item, index) => ({ name: item.name, data: [[index, item.value]] })),
                  options: {
                    ...chartData.scatterChart.options,
                    xaxis: { categories: metrics.map((item) => item.name) },
                  },
                },
                queryExecutionSpeed: {
                  series: [{ name: 'Query Execution Speed', data: additionalMetrics.map((metric) => metric.query_execution_speed) }],
                  options: {
                    ...chartData.queryExecutionSpeed.options,
                    xaxis: { categories: additionalMetrics.map((metric) => metric.timestamp) },
                  },
                },
                cpuUsage: {
                  series: [{ name: 'CPU Usage', data: additionalMetrics.map((metric) => metric.cpu_usage) }],
                  options: {
                    ...chartData.cpuUsage.options,
                    xaxis: { categories: additionalMetrics.map((metric) => metric.timestamp) },
                  },
                },
                memoryUsage: {
                  series: [{ name: 'Memory Usage', data: additionalMetrics.map((metric) => metric.memory_usage) }],
                  options: {
                    ...chartData.memoryUsage.options,
                    xaxis: { categories: additionalMetrics.map((metric) => metric.timestamp) },
                  },
                },
                responseTime: {
                  series: [{ name: 'Response Time', data: additionalMetrics.map((metric) => metric.response_time) }],
                  options: {
                    ...chartData.responseTime.options,
                    xaxis: { categories: additionalMetrics.map((metric) => metric.timestamp) },
                  },
                },
                activeConnections: {
                  series: [{ name: 'Active Connections', data: additionalMetrics.map((metric) => metric.active_connections) }],
                  options: {
                    ...chartData.activeConnections.options,
                    xaxis: { categories: additionalMetrics.map((metric) => metric.timestamp) },
                  },
                },
              });
            }
          })
          .catch((err) => console.error('Error fetching data:', err));
      };

      fetchData(); // Initial fetch
      const intervalId = setInterval(fetchData, 5000); // Poll every 5 seconds

      return () => clearInterval(intervalId); // Cleanup on component unmount
    }
  }, [chartData]);

  return (
    <div className="main-page bg-gray-900 min-h-screen p-4">
      <button
        onClick={toggleSidebar}
        className="bg-purple-600 text-white p-2 rounded-md hover:bg-purple-700 transition duration-300 transform hover:scale-105 mb-6"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-4">
        <h2 className="text-3xl font-bold text-white mb-6">Database Monitoring Dashboard</h2>

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {data.map((item, index) => (
            <div
              key={index}
              className="bg-gray-800 p-6 rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-300"
            >
              <h3 className="text-xl font-semibold text-purple-500">{item.name}</h3>
              <p className="text-gray-300">{item.value}</p>
            </div>
          ))}
        </motion.div>

        <div className="mt-12 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 xl:grid-cols-3 gap-8">
          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Database Performance Metrics</h3>
            <Chart
              options={chartData.barChart.options}
              series={chartData.barChart.series}
              type="bar"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Database Performance Over Time</h3>
            <Chart
              options={chartData.lineChart.options}
              series={chartData.lineChart.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Database Metrics Distribution</h3>
            <Chart
              options={chartData.pieChart.options}
              series={chartData.pieChart.series}
              type="pie"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Database Activity Trends</h3>
            <Chart
              options={chartData.areaChart.options}
              series={chartData.areaChart.series}
              type="area"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Metric Comparisons</h3>
            <Chart
              options={chartData.radarChart.options}
              series={chartData.radarChart.series}
              type="radar"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Heatmap Analysis</h3>
            <Chart
              options={chartData.heatmapChart.options}
              series={chartData.heatmapChart.series}
              type="heatmap"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Scatter Plot</h3>
            <Chart
              options={chartData.scatterChart.options}
              series={chartData.scatterChart.series}
              type="scatter"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Query Execution Speed</h3>
            <Chart
              options={chartData.queryExecutionSpeed.options}
              series={chartData.queryExecutionSpeed.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">CPU Usage</h3>
            <Chart
              options={chartData.cpuUsage.options}
              series={chartData.cpuUsage.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Memory Usage</h3>
            <Chart
              options={chartData.memoryUsage.options}
              series={chartData.memoryUsage.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Response Time</h3>
            <Chart
              options={chartData.responseTime.options}
              series={chartData.responseTime.series}
              type="line"
              height={350}
            />
          </div>

          <div>
            <h3 className="text-2xl font-semibold text-white mb-4">Active Connections</h3>
            <Chart
              options={chartData.activeConnections.options}
              series={chartData.activeConnections.series}
              type="line"
              height={350}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
