import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SgbdOverview = () => {
  const [sgbdData, setSgbdData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
        if (!credentials) {
          throw new Error('Database credentials not found.');
        }

        const { host, username, password } = credentials;

        const sgbdResponse = await axios.get('http://localhost:5000/api/sgbd-overview', {
          params: { host, username, password },
        });

        console.log('SGBD Response:', sgbdResponse); // Log the response

        if (sgbdResponse.data.success) {
          console.log('Fetched SGBD data:', sgbdResponse.data.sgbd); // Log the received data
          setSgbdData(sgbdResponse.data.sgbd);
        } else {
          console.error('Failed to fetch SGBD data:', sgbdResponse.data.message);
          setError(sgbdResponse.data.message);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>SGBD Overview</h1>
      {sgbdData ? (
        <div>
          {/* Display SGBD Data */}
          <p><strong>Version:</strong> {sgbdData.version}</p>
          <p><strong>Uptime:</strong> {sgbdData.uptime} seconds</p>
          <p><strong>Total Queries:</strong> {sgbdData.totalQueries}</p>
          <p><strong>Open Tables:</strong> {sgbdData.openTables}</p>
        </div>
      ) : (
        <p>Failed to load data: {error}</p>
      )}
    </div>
  );
};

export default SgbdOverview;
