import React, { useEffect, useState } from 'react';

const DemoPage = () => {
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    // Track the scroll position
    const handleScroll = () => {
      setScrollPosition(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Dynamic scroll progress bar
  const progress = (scrollPosition / (document.documentElement.scrollHeight - window.innerHeight)) * 100;

  // Intersection Observer for fade-in animations
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('opacity-100');
            entry.target.classList.add(entry.target.dataset.animation);
          } else {
            entry.target.classList.remove('opacity-100');
            entry.target.classList.remove(entry.target.dataset.animation);
          }
        });
      },
      { threshold: 0.2 }
    );

    document.querySelectorAll('.fadeIn').forEach((section) => {
      observer.observe(section);
    });

    return () => observer.disconnect();
  }, []);

  return (
    <div className="bg-gray-900 text-white min-h-screen w-full">
      {/* Scroll Progress Bar */}
      <div
        className="fixed top-0 left-0 h-1 bg-gradient-to-r from-indigo-500 to-purple-500 z-50"
        style={{ width: `${progress}%` }}
      />

      {/* Header Section */}
      <header className="relative py-40 text-center bg-gradient-to-r from-gray-800 to-black">
        <div className="absolute top-0 left-0 w-full h-full bg-opacity-60 bg-gradient-to-r from-gray-800 to-black"></div>
        <div className="relative z-10 text-white">
          <h1 className="text-5xl font-bold leading-tight tracking-wide mb-4">Bienvenue sur <span className="text-transparent bg-clip-text bg-gradient-to-r from-indigo-500 to-purple-500">DBVision</span></h1>
          <p className="text-lg text-gray-300 max-w-3xl mx-auto mt-6 leading-relaxed">
            Optimisez la gestion de vos bases de données avec des outils intuitifs et des analyses en temps réel.
          </p>
        </div>
      </header>

      {/* Features Section */}
      <section className="py-24 px-6 sm:px-8 lg:px-12 space-y-12 bg-gray-800">
        {[{
            title: "Tableaux de Bord",
            description: "Visualisez vos données en temps réel grâce à des graphiques interactifs.",
            icon: "📊",
            animation: "animate-bottom-to-top",
          },
          {
            title: "Gestion Simplifiée",
            description: "Modifiez et analysez vos données en toute simplicité.",
            icon: "⚙️",
            animation: "animate-left-to-right",
          },
          {
            title: "Mises à Jour Dynamiques",
            description: "Recevez des notifications instantanées pour suivre l’évolution de vos données.",
            icon: "⏱️",
            animation: "animate-right-to-left",
          },
        ].map((feature, index) => (
          <div
            key={index}
            data-animation={feature.animation}
            className="bg-gray-700 rounded-lg shadow-lg p-8 opacity-0 fadeIn transform transition-all duration-700 hover:bg-indigo-700"
          >
            <div className="flex items-center mb-4">
              <div className="text-3xl mr-4 text-indigo-500">{feature.icon}</div>
              <h3 className="text-2xl font-semibold text-indigo-400">{feature.title}</h3>
            </div>
            <p className="text-gray-300 text-lg">{feature.description}</p>
          </div>
        ))}
      </section>

      {/* Monitoring Section */}
      <section className="py-24 px-6 sm:px-8 lg:px-12 space-y-12 bg-gray-800 rounded-lg shadow-lg">
        <h2 className="text-3xl font-semibold text-indigo-500 text-center mb-6">
          Suivi en Temps Réel des Données
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {[{
              title: "Performance Optimale",
              icon: "📈",
              description: "Analysez les performances de votre base de données en temps réel.",
            },
            {
              title: "Utilisation du Stockage",
              icon: "💾",
              description: "Gardez un œil sur la consommation de votre stockage de données.",
            },
            {
              title: "Requêtes et Performances",
              icon: "🔍",
              description: "Étudiez les requêtes fréquentes et leurs effets sur la performance.",
            },
          ].map((item, index) => (
            <div
              key={index}
              data-animation={index % 2 === 0 ? "animate-bottom-to-top" : "animate-top-to-bottom"}
              className="bg-gray-700 p-6 rounded-lg shadow-lg opacity-0 fadeIn transform transition-all duration-700 hover:bg-indigo-700"
            >
              <div className="text-3xl mb-4 text-indigo-500">{item.icon}</div>
              <h3 className="text-2xl font-semibold text-indigo-400 mb-2">{item.title}</h3>
              <p className="text-gray-300">{item.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Call-to-Action Section */}
      <section className="flex justify-center py-12 bg-gradient-to-r from-indigo-700 to-blue-900">
        <button
          className="bg-indigo-600 hover:bg-indigo-700 text-white text-xl font-semibold py-4 px-10 rounded-full shadow-lg transform hover:scale-110 hover:shadow-2xl transition-all duration-300"
          onClick={() => window.location.href = '/login'}
        >
          Connectez Votre Base de Données
        </button>
      </section>

      {/* Footer Section */}
      <footer className="py-6 text-center bg-gray-900">
        <p className="text-gray-400 text-sm">
          DBVision - Tous droits réservés | &copy; {new Date().getFullYear()}
        </p>
      </footer>
    </div>
  );
};

export default DemoPage;
