import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

const MainPage = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const credentials = JSON.parse(localStorage.getItem('dbCredentials'));

    if (credentials) {
      // Simulate an API call or database fetch
      setData([
        { name: 'Users', value: 150 },
        { name: 'Orders', value: 1200 },
        { name: 'Products', value: 800 },
      ]);
    }
  }, []);

  return (
    <div className="bg-gray-900 text-white min-h-screen py-8 px-4">
      <h2 className="text-3xl font-bold mb-6">Dashboard</h2>
      <motion.div 
        className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        {data.map((item) => (
          <div
            key={item.name}
            className="bg-gray-800 p-6 rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-300"
          >
            <h3 className="text-xl font-semibold text-purple-500">{item.name}</h3>
            <p className="text-gray-300">{item.value}</p>
          </div>
        ))}
      </motion.div>
    </div>
  );
};

export default MainPage;
