import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const LoginPage = () => {
  const [host, setHost] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [database, setDatabase] = useState('');
  const [error, setError] = useState('');
  const history = useHistory();

  const handleLogin = (e) => {
    e.preventDefault();

    // Basic validation
    if (!host || !username || !password || !database) {
      setError('All fields are required.');
      return;
    }

    // Normally, you would make an API call here to check the database connection
    // For the purpose of the demo, we'll simulate a successful login

    // Simulating successful login
    setError('');
    localStorage.setItem('dbCredentials', JSON.stringify({ host, username, password, database }));
    history.push('/main'); // Redirect to the main page after successful login
  };

  return (
    <div className="bg-gray-900 text-white min-h-screen flex flex-col items-center justify-center p-4">
      <div className="bg-gray-800 rounded-lg shadow-lg p-8 w-full max-w-md">
        <h2 className="text-3xl font-bold text-center mb-6 text-purple-500">Connect Your Database</h2>

        {error && <p className="text-red-500 text-center mb-4">{error}</p>}

        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label className="block text-sm font-semibold text-gray-300 mb-2" htmlFor="host">
              Host
            </label>
            <input
              type="text"
              id="host"
              value={host}
              onChange={(e) => setHost(e.target.value)}
              className="w-full p-3 bg-gray-700 text-white rounded-lg"
              placeholder="Enter your database host"
            />
          </div>

          <div className="mb-4">
            <label className="block text-sm font-semibold text-gray-300 mb-2" htmlFor="username">
              Username
            </label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full p-3 bg-gray-700 text-white rounded-lg"
              placeholder="Enter your database username"
            />
          </div>

          <div className="mb-4">
            <label className="block text-sm font-semibold text-gray-300 mb-2" htmlFor="password">
              Password
            </label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-3 bg-gray-700 text-white rounded-lg"
              placeholder="Enter your database password"
            />
          </div>

          <div className="mb-4">
            <label className="block text-sm font-semibold text-gray-300 mb-2" htmlFor="database">
              Database Name
            </label>
            <input
              type="text"
              id="database"
              value={database}
              onChange={(e) => setDatabase(e.target.value)}
              className="w-full p-3 bg-gray-700 text-white rounded-lg"
              placeholder="Enter your database name"
            />
          </div>

          <div className="flex justify-center mt-6">
            <button
              type="submit"
              className="bg-blue-600 hover:bg-blue-700 text-white text-xl font-semibold py-3 px-8 rounded-full shadow-lg transform hover:scale-105 transition-transform duration-300"
            >
              Connect
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
