import React, { useState } from 'react';
import { createBrowserRouter, RouterProvider } from 'react-router-dom';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LoginPage from './components/LoginPage';
import MainPage from './components/MainPage';
import DemoPage from './components/DemoPage';


const App = () => {
  const [isSidebarVisible, setIsSidebarVisible] = useState(false); // Sidebar visibility state
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Login state

  const toggleSidebar = () => {
    setIsSidebarVisible((prev) => !prev);
  };

  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
    setIsSidebarVisible(true); // Show sidebar after login
  };

  return (
    <Router>
      <div className="flex">
        {isSidebarVisible && (
          <div className="sidebar w-64 bg-gray-800 text-white p-4">
            {/* Your sidebar content */}
            <h3>Sidebar Content</h3>
          </div>
        )}
        <div className="flex-1">
          <Routes>
            <Route
              path="/"
              element={<DemoPage />}
            />
            <Route
              path="/login"
              element={<LoginPage onLoginSuccess={handleLoginSuccess} />}
            />
            <Route
              path="/main"
              element={isLoggedIn ? <MainPage toggleSidebar={toggleSidebar} /> : <LoginPage />}
            />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
