import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SgbdMetricsChart from './SgbdMetricsChart'; // Import the chart component

const SgbdOverview = () => {
  const [sgbdData, setSgbdData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [performanceMetrics, setPerformanceMetrics] = useState(null);
const [diskIO, setDiskIO] = useState([]);
const [networkTraffic, setNetworkTraffic] = useState([]);
const [innodbMetrics, setInnodbMetrics] = useState([]);

  useEffect(() => {
    const fetchPerformanceMetrics = () => {
        fetch(`http://localhost:5000/api/performance-metrics?host=${host}&username=${username}&password=${password}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success) {
              setPerformanceMetrics(data);
            }
          })
          .catch((err) => console.error('Error fetching performance metrics:', err));
      };
      
      const fetchDiskIO = () => {
        fetch(`http://localhost:5000/api/disk-io?host=${host}&username=${username}&password=${password}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success) {
              setDiskIO(data.diskIO);
            }
          })
          .catch((err) => console.error('Error fetching disk I/O:', err));
      };
      
      const fetchNetworkTraffic = () => {
        fetch(`http://localhost:5000/api/network-traffic?host=${host}&username=${username}&password=${password}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success) {
              setNetworkTraffic(data.networkTraffic);
            }
          })
          .catch((err) => console.error('Error fetching network traffic:', err));
      };
      
      const fetchInnodbMetrics = () => {
        fetch(`http://localhost:5000/api/innodb-metrics?host=${host}&username=${username}&password=${password}`)
          .then((response) => response.json())
          .then((data) => {
            if (data.success) {
              setInnodbMetrics(data.innodbMetrics);
            }
          })
          .catch((err) => console.error('Error fetching InnoDB metrics:', err));
      };
      
      useEffect(() => {
        const credentials = JSON.parse(localStorage.getItem('dbCredentials'));
        if (credentials) {
          const { host, username, password } = credentials;
          fetchPerformanceMetrics();
          fetchDiskIO();
          fetchNetworkTraffic();
          fetchInnodbMetrics();
        }
      }, []);
    const fetchData = async () => {
      try {
        const dbCredentials = JSON.parse(localStorage.getItem('dbCredentials'));
        const response = await axios.get('http://localhost:5000/api/sgbd-overview', {
          params: { host: dbCredentials.host, username: dbCredentials.username, password: dbCredentials.password },
        });
        setSgbdData(response.data.sgbd);
      } catch (error) {
        console.error('Error fetching SGBD data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>SGBD Overview</h1>
      {sgbdData ? (
        <div>
          {/* Display SGBD Data */}
          <p><strong>Version:</strong> {sgbdData.version}</p>
          <p><strong>Uptime:</strong> {sgbdData.uptime} seconds</p>
          <p><strong>Total Queries:</strong> {sgbdData.totalQueries}</p>
          <p><strong>Open Tables:</strong> {sgbdData.openTables}</p>
          {performanceMetrics && (
        <div className="mt-5 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 xl:grid-cols-3 gap-8">
          <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl transform hover:scale-105 transition-all duration-300">
            <h3 className="text-2xl font-semibold text-white mb-4">CPU Load</h3>
            <p className="text-sm text-gray-400 mb-6">
              Current CPU load over the last minute.
            </p>
            <p className="text-lg text-white">{performanceMetrics.system.cpuLoad.toFixed(2)}%</p>
          </div>

          <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl transform hover:scale-105 transition-all duration-300">
            <h3 className="text-2xl font-semibold text-white mb-4">Memory Usage</h3>
            <p className="text-sm text-gray-400 mb-6">
              Current memory usage.
            </p>
            <p className="text-lg text-white">
              {((performanceMetrics.system.memoryUsage.used / performanceMetrics.system.memoryUsage.total) * 100).toFixed(2)}%
            </p>
          </div>
        </div>
      )}
      {innodbMetrics.length > 0 && (
        <div className="bg-gradient-to-r from-gray-800 to-gray-900 p-6 rounded-lg shadow-xl transform hover:scale-105 transition-all duration-300">
          <h3 className="text-2xl font-semibold text-white mb-4">InnoDB Metrics</h3>
          <div className="overflow-x-auto">
            <table className="min-w-full bg-gray-800 text-white rounded-lg shadow-xl">
              <thead>
                <tr>
                  <th className="px-4 py-2">Metric Name</th>
                  <th className="px-4 py-2">Metric Value</th>
                </tr>
              </thead>
              <tbody>
                {innodbMetrics.map((metric, index) => (
                  <tr key={index} className="bg-gray-700 hover:bg-gray-600">
                    <td className="px-4 py-2">{metric.metric_name}</td>
                    <td className="px-4 py-2">{metric.metric_value}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )} 
          {/* Add the chart below the SGBD data */}
          <SgbdMetricsChart data={sgbdData} />
        </div>
      ) : (
        <p>Failed to load data</p>
      )}
    </div>
  );
};

export default SgbdOverview;
