// src/MainPage.js
import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Chart from 'react-apexcharts';
import io from 'socket.io-client';

const ENDPOINT = 'http://localhost:5000';

const MainPage = ({ toggleSidebar }) => {
  const [chartData, setChartData] = useState({
    querySpeedChart: {
      series: [],
      options: {
        chart: { type: 'line', toolbar: { show: false } },
        xaxis: { categories: [] },
        theme: { palette: 'palette5' },
      },
    },
    cpuUsageChart: {
      series: [],
      options: {
        chart: { type: 'radialBar', toolbar: { show: false } },
        plotOptions: {
          radialBar: { dataLabels: { name: { show: true }, value: { show: true } } },
        },
        theme: { palette: 'palette5' },
      },
    },
    memoryUsageChart: {
      series: [],
      options: {
        chart: { type: 'radialBar', toolbar: { show: false } },
        plotOptions: {
          radialBar: { dataLabels: { name: { show: true }, value: { show: true } } },
        },
        theme: { palette: 'palette5' },
      },
    },
    activeConnectionsChart: {
      series: [],
      options: { chart: { type: 'bar', toolbar: { show: false } }, xaxis: { categories: [] } },
    },
    responseTimeChart: {
      series: [],
      options: { chart: { type: 'line', toolbar: { show: false } }, xaxis: { categories: [] } },
    },
  });

  useEffect(() => {
    const socket = io(ENDPOINT);

    socket.on('performanceData', (metrics) => {
      const timestamps = metrics.map((item) => item.timestamp);
      const querySpeedData = metrics.map((item) => parseFloat(item.query_execution_speed));
      const cpuUsageData = metrics.map((item) => parseFloat(item.cpu_usage));
      const memoryUsageData = metrics.map((item) => parseFloat(item.memory_usage));
      const activeConnectionsData = metrics.map((item) => item.active_connections);
      const responseTimeData = metrics.map((item) => parseFloat(item.response_time));

      setChartData({
        querySpeedChart: {
          series: [{ name: 'Query Speed', data: querySpeedData }],
          options: { xaxis: { categories: timestamps } },
        },
        cpuUsageChart: {
          series: [{ name: 'CPU Usage', data: [cpuUsageData.slice(-1)[0]] }],
          options: { xaxis: { categories: timestamps } },
        },
        memoryUsageChart: {
          series: [{ name: 'Memory Usage', data: [memoryUsageData.slice(-1)[0]] }],
          options: { xaxis: { categories: timestamps } },
        },
        activeConnectionsChart: {
          series: [{ name: 'Active Connections', data: [activeConnectionsData.slice(-1)[0]] }],
          options: { xaxis: { categories: timestamps } },
        },
        responseTimeChart: {
          series: [{ name: 'Response Time', data: [responseTimeData.slice(-1)[0]] }],
          options: { xaxis: { categories: timestamps } },
        },
      });
    });

    return () => socket.disconnect();
  }, []);

  return (
    <div className="main-page bg-gradient-to-r from-blue-800 via-purple-700 to-pink-600 min-h-screen p-8">
      <button
        onClick={toggleSidebar}
        className="bg-yellow-500 text-white p-3 rounded-md hover:bg-yellow-600 transition duration-300 transform hover:scale-105 mb-6"
      >
        Toggle Sidebar
      </button>

      <div className="py-8 px-4">
        <h2 className="text-4xl font-extrabold text-white mb-6">Database Performance Monitoring Dashboard</h2>

        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          {chartData.querySpeedChart.series.length > 0 && (
            <div className="chart-container">
              <h3 className="text-2xl font-semibold text-white mb-4">Query Execution Speed</h3>
              <Chart
                options={chartData.querySpeedChart.options}
                series={chartData.querySpeedChart.series}
                type="line"
                height={350}
              />
            </div>
          )}

          {chartData.cpuUsageChart.series.length > 0 && (
            <div className="chart-container">
              <h3 className="text-2xl font-semibold text-white mb-4">CPU Usage</h3>
              <Chart
                options={chartData.cpuUsageChart.options}
                series={chartData.cpuUsageChart.series}
                type="radialBar"
                height={350}
              />
            </div>
          )}

          {chartData.memoryUsageChart.series.length > 0 && (
            <div className="chart-container">
              <h3 className="text-2xl font-semibold text-white mb-4">Memory Usage</h3>
              <Chart
                options={chartData.memoryUsageChart.options}
                series={chartData.memoryUsageChart.series}
                type="radialBar"
                height={350}
              />
            </div>
          )}

          {chartData.activeConnectionsChart.series.length > 0 && (
            <div className="chart-container">
              <h3 className="text-2xl font-semibold text-white mb-4">Active Connections</h3>
              <Chart
                options={chartData.activeConnectionsChart.options}
                series={chartData.activeConnectionsChart.series}
                type="bar"
                height={350}
              />
            </div>
          )}

          {chartData.responseTimeChart.series.length > 0 && (
            <div className="chart-container">
              <h3 className="text-2xl font-semibold text-white mb-4">Response Time</h3>
              <Chart
                options={chartData.responseTimeChart.options}
                series={chartData.responseTimeChart.series}
                type="line"
                height={350}
              />
            </div>
          )}
        </motion.div>
      </div>
    </div>
  );
};

export default MainPage;
